INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (231,'Boozy Snickers Milkshake','[''vanilla ice-cream'', ''milk'', ''godiva liqueur'', ''whipped cream'', ''caramel sauce'', ''chocolate sauce'', ''mini-snickers bars'']','[''3 cups'', ''1 cup'', ''1/2 cup'', ''for topping'', ''4 tablespoons\r'', ''4 tablespoons\r'', ''15\r'']','Place the snickers bars in a plastic bag and roll over them with a rolling pin until crushed. Add crushed snickers pieces, ice cream, milk, caramel sauce, chocolate sauce, and chocolate liquor to a blender. Blend until shake is thick and frothy. Pour into glasses and top with chocolate liquor and whip cream.');
