INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (251,'Bruce''s Puce','[''grenadine'', ''kahlua'', "bailey''s irish cream"]','['''', '''', '''']','In a regular-sized shot glass, layer, with a spoon or cherry, the grenadine , the Kahlua , then the Bailey''s Irish cream in equal portions. It goes down really smooth ,and you don''t even need a chaser. It tastes just like chocolate milk.(Really!)');
