INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (258,'Bumble Bee #1','["bailey''s irish cream", ''kahlua'', ''sambuca'']','[''1/3 oz'', ''1/3 oz'', ''1/3 oz'']','This is a layered shot. First pour the Bailey''s into the shot glass. Then take an upside down spoon and touch it to the inside wall of the glass. Carefully add the Kahlua. Repeat this process for the Sambuca. If done properly, the alcohol will stay separated and resemble a bumble bee. Enjoy!!!');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (259,'Bunny Poison','[''rhubarb cucumber & mint-infused junipero gin'', ''lemon juice'', ''tempus fugit alessio vermouth bianco'', ''rhubarb juice'', ''grapefruit juice'', ''rhubarb syrup'', ''fee brothers celery bitters'']','[''2 oz'', ''.75 oz'', ''.75 oz'', ''.5 oz'', ''.5 oz'', ''.5 oz'', ''2 dash'']','Combine all ingredients in Perlini sparkling cocktail shaker, shake and double strain into cocktail glass. (For home use, combine all ingredients, add ice shake 30 seconds and double strain into coupe, add splash of soda water.). Spray with atomized Old Tom Gin.. Garnish with cucumber peel or candied rhubarb slice.. . *Rhubarb Cucumber & Mint-Infused Junipero Gin:. -- 1 bottle Junipero Gin. -- 1 qt Chopped Rhubarb. -- 1 Chopped Large English Cucumber. -- .5 Bunch (15g) Fresh Mint (with stems). . Combine all ingredients in vacuum sealed bag and circulate at 53º for 2 hours. (For home use, combine all ingredients in plastic container and let rest for one week.). . *Rhubarb Syrup:. Juice 1 qt (8-10 Stalks). Add to 1 qt of unbleached Cane sugar in saucepan and bring to simmer.. Remove from heat, allow to cool and strain in Chinoise.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (260,'Bushido Cocktail','[''nikka whisky taketsuru pure malt 12 yo'', ''pedro ximenez sherry'', ''angostura bitters'']','[''2 oz'', ''.5 oz'', ''2 dash'']','Stir ingredients and strain into cocktail glass. Garnish with Luxardo Cherry.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (261,'Buster Crusta','[''h by hine'', ''lemon juice'', ''the genuine curacao liqueur orange'', ''luxardo maraschino liqueur'', ''small hand foods gum syrup'', ''luxardo fernet'']','[''2 oz'', ''1 oz'', ''.75 oz'', ''.5 oz'', ''.5 oz'', ''1 bsp'']','Shake, fine strain into chilled frosted coupe, float Fernet.');
