INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (263,'Buzzard''s Bay','[''part no.3 london dry gin'', '''', ''part luxardo fernet'', '''', ''tea'']','[''2'', ''.5 part real cranberry juic'', ''.25'', ''.5 part lime juic'', ''1.5 part puer blac'']','Build and pour in punch bowl over large ice block.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (264,'Cafe Creole','[''pink pigeon rum'', ''luxardo espresso italian liqueur'', ''heavy cream'', ''simple syrup'']','[''1.5 oz'', ''1 oz'', ''1.5 oz'', ''.75 oz'']','In one shaker, mix heavy cream and simple syrup and dry shake vigorously until it''s whipped.. In another, shake Pink Pigeon Rum and Luxardo Espresso with ice.  Pour into a coupe, and then carefully top with the cream.  It should layer perfectly.  The vanilla complexity of the rum pairs awesomely with coffee and cream..');
