INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (269,'California Lemonade','[''blended whiskey'', ''lemon'', ''lime'', ''powdered sugar'', ''grenadine'', ''carbonated water'']','[''2 oz'', ''juice of 1'', ''juice of 1'', ''1 tblsp'', ''1/4 tsp'', '''']','Shake all ingredients (except carbonated water) with ice and strain into a collins glass over shaved ice. Fill with carbonated water and stir. Decorate with slices of orange and lemon. Add the cherry and serve with a straw.');
