INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (271,'California''s Callin''','[''junipero gin'', ''luxardo maraschino liqueur'', ''grapefruit juice'', ''lemon juice'', "tempus fugit abbott''s bitters", ''or california lager'']','[''1.5 oz'', ''.5 oz'', ''1 oz'', ''.75 oz'', ''1 dash'', ''to fill anc'']','Combine Gin, Maraschino, juices, and bitters in an ice-filled mixing tin and shake for 20-30 seconds, or until well chilled. Strain over fresh ice into a Collins glass, top with the California Lager, and garnish with the grapefruit peel.');
