INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (274,'Cap''t of the Seven Seas','[''ypioca brasilizar cachaca ouro reserva especial'', ''jalapeno-orange simple syrup'', ''coconut milk'', ''orange flower water'', ''mole bitters'']','[''2 oz'', ''1 oz'', ''1 oz'', ''4 dash'', ''9 drops'']','Combine all ingredients in a shaker tin and shake vigorously. Double strain using a fine mesh strainer. Serve in a lager glass over ice.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (275,'Caribbean Boilermaker','[''corona'', ''light rum'']','[''1 bottle'', ''1 shot'']','Pour the Corona into an 18oz beer glass pour the rum into the beer.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (276,'Caribbean Orange Liqueur','[''orange'', ''vodka'', ''sugar'']','[''3 large'', ''3 cups'', ''1 1/3 cup superfine'']','Pare very thinly the bright-colored rind from the oranges (no white). Blot the peel on paper towels to remove any excess oil. Put peel in a 4 cup screw-top jar. Add 2 cups vodka. Close jar. Store in a cool, dark place for 2 days or until the vodka has absorbed the flavor. Remove peel and add remaining vodka. Close jar and add remaining cup of vodka. Close the jar and store in a cool dark place at least 1 month to age.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (277,'Carrier Pigeon (an Airmail with Pink Pigeon)','[''pink pigeon rum'', ''lime juice'', ''honey'']','[''1 oz'', ''.5 oz'', ''.5 oz'']','Short shake-strain into champagne flute- top with champagne. . Drop a Luxardo cherry into the flute..');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (278,'Casino','[''gin'', ''maraschino liqueur'', ''lemon juice'', ''orange bitters'', ''cherry'']','[''2 oz'', ''1/4 tsp'', ''1/4 tsp'', ''2 dashes'', ''1'']','Pour all ingredients into shaker with ice cubes. Shake well. Strain into chilled cocktail glass. Garnish with a lemon twist and a maraschino cherry. Serve without a straw.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (279,'Casino Royale','[''gin'', ''lemon juice'', ''maraschino liqueur'', ''orange bitters'', ''egg yolk'']','[''2 oz'', ''1/2 oz'', ''1 tsp'', ''1 dash'', ''1'']','In a shaker half-filled with ice cubes, combine all of the ingredients. Shake well. Strain into a sour glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (280,'Castillian Hot Chocolate','[''cocoa powder'', ''sugar'', ''cornstarch'', ''water'', ''milk'']','[''1/2 cup'', ''1 cup'', ''7 tsp'', ''1/2 cup'', ''1 qt'']','Shift the cocoa and sugar together into a medium-sized saucepan. Dissolve the cornstarch in the water, and stir into the cocoa and sugar until it is a smooth paste. Begin heating the mixture, stirring it with a whisk, and gradually pour in the milk. Continue stirring with the whisk as you bring the liquid to a simmer. Allow the chocolate to simmer for about 10 minutes, stirring often, until it is thick, glossy and completely smooth. Serve steaming hot in coffee mug. Serves six.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (281,'Champagne Cocktail','[''champagne'', ''sugar'', ''bitters'', ''lemon peel'', ''cognac'']','[''chilled'', ''1 piece'', ''2 dashes'', ''1 twist of'', ''1 dash'']','Add dash of Angostura bitter onto sugar cube and drop it into champagne flute. Add cognac followed by gently pouring chilled champagne. Garnish with orange slice and maraschino cherry.');
