INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (285,'Charente Royale','[''h by hine'', ''elderflower liqueur'', ''absinthe'', ''sparkling wine'']','[''1.5 oz'', ''.5 oz'', ''2 dash'', ''top'']','Combine all ingredients except sparkling wine in a shaker. Add ice and shake until chilled through. Double strain into a champagne flute and garnish with an orange twist.');
