INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (287,'Cherry Bark Martinez','[''old tom gin'', ''carpano antica'', ''luxardo maraschino liqueur'', ''angostura bitters'', ''bittercube cherry bark bitters'']','[''2 oz'', ''.75 oz'', ''1 spoon'', ''1 dash'', ''2 dash'']','Stir and strain into stem. Garnish torched star anise and discarded lemon peel.');
