INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (289,'Cherry Champagne Cocktail','[''champagne or prosecco'', ''tart cherry juice'', ''luxardo maraschino liqueur'', ''mole bitters'', ''luxardo amaro abano'']','[''4 oz'', ''1 oz'', ''.5 oz'', ''4 dash'', ''1 bsp'']','Stir the cherry juice, Maraschino, bitters and Amaro in a mixing glass with ice. Strain into a cocktail coupe and top with Champagne. Garnish with a sprig of thyme.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (290,'Cherry Electric Lemonade','[''gin'', ''tequila'', ''vodka'', ''white rum'', ''triple sec'', ''cherry grenadine'', ''sweet and sour'', ''club soda'']','[''1 oz'', ''1 oz'', ''1 oz'', ''1 oz'', ''1 oz'', ''1 oz'', ''1 oz'', ''3 oz'']','Now stir vigorously and then pour over a large cup of ice. Now drink it with a straw and stir occasionally.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (291,'Cherry Old Fashioned','[''demerara sugar'', ''roasted cherry'', ''cherry-infused vodka'', ''amaro di angostura'', ''luxardo maraschino liqueur'', ''miracle mile forbidden barrel aged bitters'']','[''.5 tsp'', ''1'', ''2 oz'', ''.25 tsp'', ''.25 tsp'', ''5 dash'']','Muddle sugar and 1 roasted cherry in a rocks glass until the sugar has dissolved; add vodka, Amaro, Maraschino, and bitters and stir. Add 1 large ice cube and garnish with half a roasted cherry and the tarragon.. . Cherry-Infused Vodka:. - 1 cup pitted cherries, quartered. - 1 cup vodka. Combine cherries and vodka in a bottle; cover, and let sit to meld the flavors for 3-5 days.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (292,'Cherry Orchard','[''luxardo sangue morlacco cherry liqueur'', ''luxardo maraschino liqueur'', "laird''s straight bonded", ''clear creek kirschwasser eau de vie'', ''lemon juice'', ''grapefruit juice'', ''simple syrup'', "peychaud''s bitters"]','[''.5 oz'', ''.25 oz'', ''.5 oz'', ''1.25 oz'', ''.25 oz'', ''.5 oz'', ''.25 oz'', ''2 dash'']','Shake over ice. Strain into a 6 oz. cocktail glass');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (293,'Cherry Rum','[''light rum'', ''cherry brandy'', ''light cream'']','[''1 1/4 oz'', ''1 1/2 tsp'', ''1 tblsp'']','Shake all ingredients with ice, strain into a cocktail glass, and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (294,'Cherry Shandy','[''luxardo sangue morlacco cherry liqueur'', ''ipa'']','[''1.5 oz'', ''6 oz'']','Stir IPA and Luxardo in a tumbler. Garnish with Luxardo Cherry.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (295,'Chicago Fizz','[''light rum'', ''port'', ''lemon'', ''powdered sugar'', ''egg white'', ''carbonated water'']','[''1 oz'', ''1 oz'', ''juice of 1/2'', ''1 tsp'', ''1'', '''']','Shake all ingredients (except carbonated water) with ice and strain into a highball glass over two ice cubes. Fill with carbonated water, stir, and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (296,'Chilcano','[''barsol selecto acholado pisco'', ''fresh lime juice'', ''angostura bitters'', ''ginger ale'']','[''2 oz'', ''.5 oz'', ''2-3 drops'', ''top'']','Add pisco and lime juice into a highball glass filled with ice. Pour ginger ale into the glass to top. Add 2-3 drops of bitters. Garnish with lime wheel.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (297,'Chilcano (variation)','[''barsol primero quebranta pisco'', "king''s ginger liqueur", ''fresh lime juice'', ''agave nectar'', ''cranberry bitters'', ''ginger beer'']','[''1.25 oz'', ''.25 oz'', ''.5 oz'', ''.25 oz'', ''3-4 drops'', ''4 oz'']','Build in an ice filled 14 oz beer glass, add BarSol Pisco, Ginger Liqueur, fresh lime juice, and agave nectar. Roll into a Boston shaker and shake to mix, place cranberry bitters on top, add ginger beer and garnish with a wedge of fresh lime.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (298,'Chilli Vanilli','["karlsson''s gold vodka", ''lustau amontillado sherry'', ''st. elizabeth allspice dram'', ''burned demerara sugar syrup*'', "ure bob''s vanilla bitters", ''vanilla powder'', ''egg white'']','[''2 oz'', ''.5 oz'', ''.75 oz'', ''.75 oz'', ''1 tinc'', ''.75 oz'', ''1'']','In a Boston shaker combine all liquid ingredients in a shaker without ice and dry shake for 10-12 shakes to blend the egg whites. Once blended add cracked ice and shake vigorously for 30 seconds and strain into a frozen 4.5 oz. coupe glass. Let the cocktail settle and sprinkle with the vanilla powder.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (299,'Chingerito','[''barsol primero quebranta pisco'', ''grape juice'', ''barsol perfecto amor (aperitif wine)'', ''fresh pomegranate juice'', ''cinnamon syrup'', ''fresh lime juice'', ''grapes'']','[''2 oz'', ''1.5 oz'', ''1 oz'', ''1 oz'', ''.75 oz'', ''.75 oz'', ''4'']','Shake and strain into ice filled glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (300,'Chippendale Rosetone','[''part gin'', ''t liqueur'', ''part casoni 1814'', ''part dry vermouth'', '''', '''', ''simple syrup (2:1)'', ''part jasmine green tea'']','[''1.5'', ''.25 part luxardo apric'', ''.75'', ''.5'', ''1 part unfiltered apple juic'', ''1 part fresh lemon juic'', ''.5 part ric'', ''2'']','Combine all ingredients in a punch bowl, add a large chunk or block of ice, garnish with lemon wheels and apple slices. Serve when punch is chilled.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (301,'Chocolate Beverage','[''milk'', ''chocolate'', ''cinnamon'', ''egg'']','[''6 cups'', ''3 oz mexican'', ''1 tsp powdered'', ''3'']','Boil milk in the top of a deep double boiler five minutes. Remove from fire and add chocolate, mixed with the cinnamon, a little at a time, beating with molinillo or egg beater after each addition. When the chocolate is thoroughly blended, heat to the boiling point. Place over bottom of double boiler and add eggs, whipping constantly, until they are thoroughly blended and the mixture is frothing. Serve in coffee mug. Serves eight.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (302,'Chocolate Black Russian','[''kahlua'', ''vodka'', ''chocolate ice-cream'']','[''1 oz'', ''1/2 oz'', ''5 oz'']','Combine all ingredients in an electric blender and blend at a low speed for a short length of time. Pour into a chilled champagne flute and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (303,'Chocolate Drink','[''chocolate'', ''milk'', ''water'']','[''125 gr'', ''3/4 l'', '' '']','Melt the bar in a small amount of boiling water. Add milk. Cook over low heat, whipping gently (with a whisk, i would assume) until heated well. Don''t let it boil! Serve in coffee mug.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (304,'Chocolate Milk','[''chocolate liqueur'', ''milk'', ''amaretto'']','[''1/2 shot'', ''1/2 shot'', ''1 dash'']','Put the milk in the bottom, pour the Liquer on top and add the dash of amaretto. Do not mix. SLAM IT!');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (305,'Chocolate Monkey','[''banana liqueur'', ''creme de cacao'', ''chocolate ice-cream'', ''chocolate syrup'', ''chocolate milk'', ''whipped cream'', ''cherry'', ''banana'']','[''1 shot'', ''2 shots'', ''2 scoops'', ''1 oz'', ''4 oz'', ''1'', ''1'', ''1 piece'']','blend liqeuors with ice-cream, milk and syrup. pour into parfait glass, top with whipped cream and garnish with banana and cherry.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (306,'Château Montifaud Creation','[''chateau de montifaud cognac vs'', ''lemon juice'', ''orgeat (almond) syrup'', ''triple sec'', ''lemonade'']','[''1.5 oz'', ''1 oz'', ''.25 oz'', ''.25 oz'', ''top'']','Pour the Cognac and lemon juice into the glass and then add orgeat syrup; fill with ices cubes and mix with a spoon. At the end, add lemonade and lemon peel.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (307,'Château Montifaud Lady Touch Triple Sec','[''chateau de montifaud reserve speciale michel vallet'', ''triple sec'', ''lemon juice'', ''sugar syrup'', ''fresh mint'', ''cubes'']','[''1.5 oz'', ''.75 oz'', ''1 oz'', ''1 oz'', ''8'', ''ic'']','Pour the ingredients in the shaker. Close and shake well until frosted. Strain into the glass. Garnish with fresh mint.');
