INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (309,'Château Montifaud on Mint','[''sugar syrup'', ''fresh mint'', ''d green lemon'', ''chateau de montifaud cognac vsop'', ''lemon juice'', ''sparkling water'']','[''1 coffee spoon'', ''10'', ''.25  slic'', ''1 oz'', ''.75 oz'', ''.75 oz'']','Pour the sugar syrup into the glass and add fresh mint and green lemon. Lightly crush and add ice cubes, Cognac, lemon juice and sparkling water. Mix with a spoon and serve with a straw.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (310,'Château Montifaud on the Rocks','[''chateau de montifaud cognac xo'', ''cubes'']','[''1.5 oz'', ''3  ic'']','Pour the Cognac into the glass and add ice cubes. Perceive the aromas that are released as a summer rain that reveals the secrets of the nature...');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (311,'Citrus Agave Caipirinha','[''ypioca brasilizar cachaca prata classica'', ''citrus agave blend'', ''soda water'']','[''1.5 oz'', ''1.5 oz'', ''1 splash'']','Add soda to bucket first, then add spirits and mixers. Top with ice and garnish.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (312,'Citrus Coke','[''bacardi limon'', ''coca-cola'']','[''1 part'', ''2 parts'']','Pour half of coke in a glass. Then add Bacardi and top it off with the remaining coke. Stir and drink up!');
