INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (323,'Cocao Martini','["karlsson''s gold vodka", ''ginger brown*'', ''hella hula bitters'', ''o beans'']','[''2.5 oz'', ''.5 oz'', ''4 dash'', ''3 beans coc'']','Muddle 3 cacao beans in a mixing glass, add vodka, ginger-brown sugar, and Bitter Beast Hella Hula bitters. Add ice and vigorously shake to incorporate cacao flavor. Fine strain into a martini glass. Garnish with a cocao bean.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (324,'Cock-tail','[''junipero gin'', ''tempus fugit alessio vermouth di torino rosso'', ''drambuie'', ''rich brown sugar simple syrup*'', ''s candied ginger'', ''angostura bitters'']','[''1.5 oz'', ''.75 oz'', ''.25 oz'', ''.25 oz'', ''4  large piec'', ''3 dash'']','Shake all ingredients and strain into a coupe glass. Express a lemon peel over the drink and discard.. . *Rich Brown Sugar Simple Syrup: . -- 2 cups Brown Sugar. -- 1 cup Water. . Combine sugar and water in a small saucepan over medium heat and stir until sugar dissolves. Remove from heat, let cool to room temperature and transfer to a clean glass jar. Cover and keep refrigerated for up to 2 weeks.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (325,'Coco Batida','[''coconut milk'', ''condensed milk'', ''ypiòca'', ''sugar'']','[''1 1/2 oz'', ''1 1/2 oz'', ''3 oz'', ''1 tbsp'']','Put 1 1/2 ounces of coconut milk, 1 1/2 ounces of condensed milk, 3 ounces of Ypiòca and tablespoon of sugar in a blender. Mix for a few seconds. Add ice cubes and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (326,'Coffee & Clove','[''nikka coffey grain whisky'', ''manzanilla sherry'', ''orange liqueur'', ''clove tincture'']','[''1.5 oz'', ''.75 oz'', ''.75 oz'', ''2 dash'']','Stir. Strain into Nick & Nora glass. Mist of Pipe Tobacco Tincture.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (327,'Coffee Liqueur','[''coffee'', ''vanilla extract'', ''sugar'', ''vodka'', ''water'']','[''10 tblsp instant'', ''4 tblsp'', ''2 1/2 cups'', ''1 qt'', ''2 1/2 cups'']','Combine coffee, sugar and water. Simmer 1 hour and let cool. Add vanilla and vodka. Age in sealed jar 2 to 3 weeks.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (328,'Coffee-Vodka','[''water'', ''sugar'', ''coffee'', ''vanilla'', ''vodka'', ''caramel coloring'']','[''2 cups'', ''2 cups white'', ''1/2 cup instant'', ''2-jan'', ''1 1/2 cup'', '''']','Boil water and sugar until dissolved. Turn off heat. Slowly add dry instant coffee and continue stirring. Add a chopped vanilla bean to the vodka, then combine the cooled sugar syrup and coffee solution with the vodka. Cover tightly and shake vigorously each day for 3 weeks. Strain and filter. Its also best to let the sugar mixture cool completely so the vodka won''t evaporate when its added. If you like a smoother feel to the liqueur you can add about 1 teaspoon of glycerine to the finished product.');
