INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (338,'Cranberry Cordial','[''cranberries'', ''sugar'', ''light rum'']','[''1/2 kg chopped'', ''3/4 l'', ''1/2 l'']','Place the chopped cranberries in a 2 liter jar that has a tight-fitting lid. Add the sugar and rum. Adjust the lid securely and place the jar in a cool, dark place. Invert the jar and shake it every day for six weeks. Strain the cordial into bottles and seal with corks.');
