INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (341,'Cream of the Crop','[''mezcal'', "king''s ginger liqueur", ''coconut pistachio puree*'', ''lemon juice'']','[''1 oz'', ''1 oz'', ''.75 oz'', ''.75 oz'']','Shake with ice in a mixing glass for about 15 seconds to properly chill. Double strain into a small fizz glass over ice.. . *Coconut Pistachio Puree:. -- .5 lb Pistachios (toasted). -- 1.5 lbs Sugar (144 oz.). -- 11 oz Coconut Milk. -- 13 oz Water. -- 1 tsp Salt. . Toast the pistachios at 250°F for 20 mins. Remove and put in a blender with sugar until well chopped. Combine all the ingredients in a pot and bring to a boil. Let simmer for 20 mins and then remove from the heat. Let cool and place in a large container. Let the mixture sit overnight then strain out the solids. Save the solids to spin in the ice cream maker (if you have one). Spin the solids and add the mixture back into the puree via chinois. Bottle, label and date the syrup (it will keep indefinitely if you freeze it).');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (342,'Creek Ran Dry','[''pear brandy'', ''dry vermouth'', ''chateau de montifaud pineau des charentes blanc'', ''dolin genepy'']','[''1 oz'', ''1 oz'', ''.5 oz'', ''.5 oz'']','Barrel aged for 5 months in a used bourbon barrel.. . Stir with ice and strain into a small coupe. Garnish with an orange twist.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (343,'Creme de Menthe','[''sugar'', ''water'', ''grain alcohol'', ''peppermint extract'', ''food coloring'']','[''8 cups'', ''6 cups'', ''1 pint'', ''1 oz pure'', ''1 tblsp green'']','Bring sugar and water to a boil and simmer for 10 minutes. Cool. Add the remaining ingredients and stir. Cover and let ripen for 1 month.');
