INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (349,'Cuba Liberation','[''pink pigeon rum'', ''fresh lime juice'', ''simple syrup'', ''mexican coca cola'', ''luxardo fernet'']','[''2 oz'', ''1.5 oz'', ''.25 oz'', ''oz'', ''1 float'']','Add first three ingredients to a mixing glass with shaker and lightly shake with ice. Pour mixture into a Collins or highball glass over ice. Top with Mexican Coke (or any coca cola as a substitute) and pour Luxardo Fernet on top to float. Garnish with a lime wheel and fresh mint sprig.');
