INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (353,'Cullerton Street','[''barley tea-infused nikka coffey grain whisky*'', ''verjus blanc'', ''kummel liqueur'', ''pineau des charentes'', ''demerara sugar syrup'']','[''2 oz'', ''.25 oz'', ''.25 oz'', ''.75 oz'', ''1 tsp'']','Stir. Add ice cube.. . *Barley Tea-Infused Nikka Coffey Grain Whiskey:. -- 10 g. Barley Tea (1 package). -- 750 mls. Nikka Coffey Grain Whisky. In a cambro, empty a bottle of Nikka Coffey Grain Whiskey, add 1 package of barley tea.. Steep for 30 minutes at room temperature.. Strain and store.');
