INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (364,'Darkwood Sling','[''cherry heering'', ''soda water'', ''orange juice'', ''ice'']','[''1 part'', ''1 part'', ''1 part'', ''cubes'']','There are many good cherry liqueurs you can use, but I prefere Heering. Add one share of the liqueur. Then you add one share of Soda. For a sour sling use Tonic (most people prefer the drink without Tonic). Afterwards you fill the glass with Orange Juice and ice cubes.');
