INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (380,'Don''t Rock the Boat Punch','[''part no.3 london dry gin'', ''oled)'', ''oled)'', ''part clover honey syrup'', ''part cava'', ''angostura'']','[''1'', ''1 part rooibus tea (steeped and c'', ''1 part mulled cider (c'', ''.5'', ''.33'', ''angostur'']','Combine ingredients into a chilled punch bowl, ice and dilute appropriately, garnish seasonally.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (381,'Donaji','[''mezcal'', ''fresh lemon juice'', ''fresh lime juice'', ''agave nectar'']','[''2 oz'', ''.5 oz'', ''.5 oz'', ''.75 oz'']','Shaken and strained over ice in a rocks glass.. . *Chapuline, Salt & Chile Pepper Rim:. Grind the chapuline  (dried crickets) and dried chile pepper in a mortar and pestle, as it becomes a powder, throw in the salt and pour onto a plate. Use a fresh lime wedge to moisten the rim of the glass and dip into the powder.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (382,'Double Trouble','[''junipero gin'', ''strawberry & black pepper shrub*'', ''hidalgo la gitana manzanilla sherry'', ''lemon juice'', "peychaud''s bitters"]','[''1.5 oz'', ''1 oz'', ''.75 oz'', ''.5 oz'', ''few drops'']','Shake with ice and strain into a footed pilsner glass. Add a few drops of bitters. Garnish with a mint leaf. . . *Strawberry & Black Pepper Shrub:. -- 1 cup Sliced Strawberries. -- 1 cup Granulated Sugar. -- 1 cup Champagne Vinegar. -- 1 handful of Black Balinese Peppers. . Combine all ingredients in a medium bowl. Using a potato masher or a wooden spoon, mash the berries so they release their juices. Stir until the sugar has mostly dissolved and refrigerate overnight.. The next day, remove the mixture to a saucepan and heat gently over medium-low heat. Do not bring to a simmer--you simply want to heat the mixture to coax the sugar to dissolve.. Strain the shrub through a fine mesh strainer. If desired, strain again through a coffee filter. Refrigerate.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (383,'Down the Rabbit Hole','[''junipero gin'', ''lemon'', ''elder flower liquor'', ''honey syrup'', ''carrot juice'']','[''oz'', ''oz'', ''oz'', ''oz'', ''oz'']','Add all ingredients into a shaker, shake strain into a collins glass, fill with crushed ice, garnish with mint leaves.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (384,'Downshift','[''fruit punch'', ''sprite'', ''tequila'', ''151 proof rum'']','[''2 part'', ''1 part'', ''2 shots'', ''float bacardi'']','Start with the Sprite. Next comes the tequila. After that, add the Minute Maid Fruit Punch, then float the 151. Rocks optional.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (385,'Dragonfly','[''gin'', ''ginger ale'', ''lime'']','[''1 1/2 oz'', ''4 oz'', ''1'']','In a highball glass almost filled with ice cubes, combine the gin and ginger ale. Stir well. Garnish with the lime wedge.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (386,'Drink Lab #16','[''brandy'', ''orgeat'', ''luxardo sangue morlacco cherry liqueur'', ''lemon juice'', ''bittermens burlesque bitters'']','[''2 oz'', ''.75 oz'', ''.75 oz'', ''.5 oz'', ''10 drops'']','Shake, strain, lemon twist garnish.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (387,'Drinking Chocolate','[''heavy cream'', ''milk'', ''cinnamon'', ''vanilla'', ''chocolate'', ''whipped cream'']','[''2 oz'', ''6-8 oz'', ''1 stick'', ''1'', ''2 oz finely chopped dark'', ''fresh'']','Heat the cream and milk with the cinnamon and vanilla bean very slowly for 15-20 minutes. (If you don''t have any beans add 1-2 tsp of vanilla after heating). Remove the bean and cinnamon. Add the chocolate. Mix until fully melted. Serve topped with some very dense fresh whipped cream. Serves 1-2 depending upon how much of a glutton you are. For a richer chocolate, use 4 oz of milk, 4 oz of cream, 4 oz of chocolate. Serve in coffee mug.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (388,'Drops of Jupiter','[''junipero gin'', ''genevieve gin'', ''rich wild sage honey syrup'', ''angostura bitters'', ''whiskey barrel aged bitters'', ''spring water'']','[''1.5 oz'', ''.5 oz'', ''.25 oz'', ''1 dash'', ''1 dash'', ''.75 oz'']','Combine all ingredients except Duke''s Lemon garnish in mixing glass and stir. Pour into a rocks glass and garnish. Ice does not touch this drink.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (389,'Dry Mauritian Sour','[''pink pigeon rum'', ''campari'', ''lavender syrup'', ''lemon juice'', ''orange juice'', ''egg white'']','[''40 ml'', ''10 ml'', ''10 ml'', ''10 ml'', ''10 ml'', ''25 ml'']','Dry shake, shake with cubed ice, double strain into chilled glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (390,'Dry Rob Roy','[''scotch'', ''dry vermouth'', ''lemon peel'']','[''2 1/2 oz'', ''1 1/2 tsp'', ''1 twist of'']','In a mixing glass half-filled with ice cubes, combine the Scotch and vermouth. Stir well. Strain into a cocktail glass. Garnish with the lemon twist.');
