INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (393,'Duke''s Classic Dry Martini','[''no.3 london dry gin'', ''extra dry vermouth'', ''amalfi lemon'']','[''3 oz'', ''1 tsp'', ''1'']','Pour the Extra Dry Vermouth into a frozen martini glass (either 7 oz. or 5.5 oz. glass) and coat in a circular motion. Top up the glass with ice cold No.3 gin. Pare the rind of an Amalfi lemon, and give it a twist to extract the oils into the glass.');
