INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (395,'Eat, Drink and Be Cherry','[''nikka coffey grain whisky'', ''luxardo sangue morlacco cherry liqueur'', ''mendolera'', ''cane sugar'', ''lemon'', ''fever-tree soda'']','[''.75 oz'', ''.75 oz'', ''.5 oz'', ''.5 oz'', ''.5 oz'', ''3 oz'']','Shake everything except for the soda water, strain into bamboo highball, add soda, add ice, garnish with 3 picked cherries.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (396,'Eden''s Apple','["apple & cinnamon-infused karlsson''s gold vodka", "j. t. taylor''s falernum", ''fresh lemon juice'', ''fresh cider'', ''ure bar keep baked apple bitters'']','[''1.5 oz'', ''.75 oz'', ''.5 oz'', ''1 oz'', ''1 tinc'']','In a Boston shaker, combine all liquid ingredients with cracked ice and shake vigorously for 30 seconds. Double strain into a frozen 4.5 oz. coupe glass. Garnish with the apple slice. Can also be served warm.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (397,'Egg Cream','[''chocolate syrup'', ''milk'', ''soda water'']','[''2 tblsp'', ''6 oz whole'', ''6 oz'']','Mix syrup and milk in a fountain glass. Add soda water, serve with a straw.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (398,'Egg Nog #4','[''egg yolk'', ''sugar'', ''milk'', ''light rum'', ''bourbon'', ''vanilla extract'', ''salt'', ''whipping cream'', ''egg white'', ''sugar'', ''nutmeg'']','[''6'', ''1/4 cup'', ''2 cups'', ''1/2 cup'', ''1/2 cup'', ''1 tsp'', ''1/4 tsp'', ''1 cup'', ''6'', ''1/4 cup'', ''ground'']','In a small mixer bowl beat egg yolks till blended. Gradually add 1/4 cup sugar, beating at high speed till thick and lemon colored. Stir in milk, stir in rum, bourbon, vanilla, and salt. Chill thoroughly. Whip cream. Wash beaters well. In a large mixer bowl beat egg whites till soft peaks form. Gradually add remaining 1/4 cup sugar, beating to stiff peaks. Fold yolk mixture and whipped cream into egg whites. Serve immediately. Sprinkle nutmeg over each serving. Serve in a punch bowl or another big bowl. NOTE: For a nonalcoholic eggnog, prepare Eggnog as above, except omit the bourbon and rum and increase the milk to 3 cups.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (399,'Egg Nog - Healthy','[''egg'', ''sugar'', ''condensed milk'', ''milk'', ''vanilla extract'', ''rum'', ''nutmeg'']','[''1/2 cup'', ''3 tblsp'', ''13 oz skimmed'', ''3/4 cup skimmed'', ''1 tsp'', ''1 tsp'', '''']','Whip egg substitute and sugar together, combine with the two kinds of milk, vanilla, and rum. Mix well. Chill over night. Sprinkle with nutmeg. Makes 6 servings.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (400,'Egg-Nog - Classic Cooked','[''egg'', ''sugar'', ''salt'', ''milk'', ''vanilla extract'']','[''6'', ''1/4 cup'', ''1/4 tsp'', ''1 qt'', ''1 tsp'']','In large saucepan, beat together eggs, sugar and salt, if desired. Stir in 2 cups of the milk. Cook over low heat, stirring constantly, until mixture is thick enough to coat a metal spoon and reaches 160 degrees F. Remove from heat. Stir in remaining 2 cups milk and vanilla. Cover and regfigerate until thoroughly chilled, several hours or overnight. Just before serving, pour into bowl or pitcher. Garnish or add stir-ins, if desired. Choose 1 or several of: Chocolate curls, cinnamon sticks, extracts of flavorings, flavored brandy or liqueur, fruit juice or nectar, ground nutmeg, maraschino cherries, orange slices, peppermint sticks or candy canes, plain brandy, run or whiskey, sherbet or ice-cream, whipping cream, whipped. Serve immediately.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (401,'El Cortez','[''luxardo apricot liqueur'', ''chinaco reposado tequila'', ''dry riesling'']','[''1 oz'', ''1.5 oz'', ''2 oz'']','Stir the first two ingredients with ice, then strain into a chilled wine glass. Top with chilled wine, stir and serve.');
