INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (408,'English Harbour Colada','[''english harbour rum 5 yo'', ''fresh squeezed pineapple juice'', ''fresh coconut water'']','[''2 oz'', ''2 oz'', ''2 oz'']','Build in old fashioned glass over ice.');
