INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (410,'English Rose Cocktail','[''apricot brandy'', ''gin'', ''dry vermouth'', ''grenadine'', ''lemon juice'', ''cherry'']','[''3/4 oz'', ''1 1/2 oz'', ''3/4 oz'', ''1 tsp'', ''1/4 tsp'', ''1'']','Rub rim of cocktail glass with lemon juice and dip rim of glass in powdered sugar. Shake all ingredients (except cherry) with ice and strain into sugar-rimmed glass. Top with the cherry and serve.');
