INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (414,'Essence No.5 (Adapted)','[''no.3 london dry gin'', ''luxardo triplum (triple sec)'', ''lavander syrup'', ''fresh lime juice'', ''raspberries'']','[''1.5 oz'', ''.75 oz'', ''.5 oz'', ''1 oz'', ''4'']','Shake all ingredients with ice then double strain into a chilled cocktail glass. Garnish with channeled lemon and raspberry on the rim.');
