INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (417,'Farmer''s Gold','["karlsson''s gold vodka", ''fresh lemon juice'', ''simple syrup (1:1)'', "regans'' orange bitters", ''large basil leaves'', ''black peppercorns'']','[''2 oz'', ''.75 oz'', ''1 oz'', ''3 dash'', ''5'', ''16 whole'']','Muddle 4 basil leaves, 16 peppercorns and simple syrup in shaker tin. Add all other ingredients except final basil leaf. Add ice. Shake vigorously. Fine strain into chilled cocktail glass. Express lemon oil from lemon swath over drink. Garnish with lemon swath and final basil leaf.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (418,'Feingold','[''brandy'', ''orange cordial'', ''luxardo sangue morlacco cherry liqueur'', ''luxardo cherry (muddled)'', ''aromatic bitters'', ''cherry bitters'']','[''2 oz'', ''.5 oz'', ''1 bsp'', ''1'', ''1 dash'', ''1 dash'']','Muddle cherry in liquid ingredients. Shake, double strain over cracked ice. Garnish with two more cherries on a pick with an orange flag.');
