INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (485,'Glimpse of Seduction','[''luxardo maraschino liqueur'', ''grapefruit flavored vodka'', ''fresh lemon juice'', ''pomegranate liqueur'', ''op lemon sorbet'']','[''.75 oz'', ''.75 oz'', ''.5 oz'', ''1 float'', ''2 sc'']','Pour the first four ingredients in a blender and blend until smooth. Serve into a champagne saucer. Top with Pomegranate Liqueur float.');
