INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (491,'Gold Rush','[''basil leaves'', "karlsson''s gold vodka", ''pineapple juice'', ''simple syrup'', ''sweet vermouth'', ''lime juice'', ''egg white'', ''angostura bitters'']','[''3'', ''1.5 oz'', ''.75 oz'', ''.5 oz'', ''.5 oz'', ''.5 oz'', ''.5 oz'', ''1 dash'']','Put basil leaves in mixing glass. Gently muddle the leaves. Add all ingredients except the bitters or ice. Dry shake the cocktail vigorously. Add the ice and shake again vigorously. Double strain into a coupe. Add a few drops of angostura on top, add cracked pepper, slap one single basil leaf on top for garnish.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (492,'Gold Spritz','[''cocchi americano'', "karlsson''s gold vodka", ''club soda'']','[''1 oz'', ''1 oz'', ''2 oz'']','Combine all ingredients in a glass filled with ice and garnish with an orange twist.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (493,'Golden Gun','[''blended lightly aged rum'', ''blended aged rum'', ''lime juice'', ''luxardo apricot liqueur'', ''sc demerara syrup*'', ''grapefruit juice'', ''angostura bitters'']','[''1 oz'', ''1 oz'', ''.75 oz'', ''.5 oz'', ''.5 oz'', ''.5 oz'', ''2 dash'']','Fill a collins or highball glass with cracked or cubed ice. Add all the ingredients to a cocktail shaker filled with cracked or cubed ice. Shake and strain into the glass.. . *SC Demerara Syrup:. -- 2 C Water. -- 1 C Demerara Sugar. -- 3 C Granulated Sugar. . Bring water to boil in a saucepan over high heat. Add the demerara sugar and stir vigorously with a whisk (or use an immersion blender) until sugar is dissolved, about 1 minute (the water should become clear). Add granulated sugar and stir vigorously until dissolved, about 1 minute. Remove from heat and let cool.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (494,'Golden dream','[''galliano'', ''triple sec'', ''orange juice'', ''cream'']','[''2 parts'', ''2 parts'', ''2 parts'', ''1 part'']','Shake with cracked ice. Strain into glass and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (495,'Gran Classico Negroni','[''tempus fugit gran classico bitter'', ''no.3 london dry gin'', ''tempus fugit alessio vermouth di torino rosso'']','[''.75 oz'', ''.75 oz'', ''.75 oz'']','Strain into an ice filled glass and garnish with an orange twist.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (496,'Grand Blue','[''malibu rum'', ''peach schnapps'', ''blue curacao'', ''sweet and sour'']','[''1 1/2 cl'', ''1 1/2 cl'', ''1 1/2 cl'', ''3 cl'']','Serve in an old fashioned glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (497,'Grape lemon pineapple Smoothie','[''grapes'', ''lemon'', ''pineapple'']','[''1 cup'', ''4-jan'', ''2-jan'']','Throw everything into a blender and liquify.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (498,'Grass Skirt','[''gin'', ''triple sec'', ''pineapple juice'', ''grenadine'', ''pineapple'']','[''1 1/2 oz'', ''1 oz'', ''1 oz'', ''1/2 tsp'', ''1'']','In a shaker half-filled with ice cubes, combine the gin, triple sec, pineapple juice, and grenadine. Shake well. Pour into an old-fashioned glass and garnish with the pineapple slice.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (499,'Grasshopper','[''green creme de menthe'', ''creme de cacao'', ''light cream'']','[''3/4 oz'', ''3/4 oz white'', ''3/4 oz'']','Pour ingredients into a cocktail shaker with ice. Shake briskly and then strain into a chilled cocktail glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (500,'Green Goblin','[''cider'', ''lager'', ''blue curacao'']','[''1/2 pint hard'', ''1/2 pint'', ''1 shot'']','Cider First, Lager then Curacao');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (501,'Green and Gold','["bottle karlsson''s gold vodka", ''sprigs dill'', ''oves crushed garlic'', ''lemon peel (white pith removed and julienned)'', ''whole peppercorns'']','[''750 ml'', ''5'', ''2 c'', ''1'', ''1 tbsp'']','Step one: Infuse the vodka. - Transfer ingredients including 750 ml of Karlsson''s vodka to a canning jar or glass container. Close the container.. - Shake and rotate every few hours. Let infuse for 24 hours total.. - After 24 hours, strain the vodka through a cheesecloth or nutmilk bag.. - Transfer the vodka to a glass bottle. Store in the refrigerator or freezer until use.. . Step two: Make the martini. - Stir 2-3 oz, depending on preference, of the infused vodka with ice.. - Strain into a chilled martini glass.. - Add pickled onions and peppers.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (502,'Grey Anchor','[''hophead vodka'', ''lime juice'', ''st. germaine elderflower liqueur'', ''luxardo maraschino liqueur'', ''bittercube door county hop bitters'']','[''2 oz'', ''.75 oz'', ''.5 oz'', ''.25 oz'', ''5 drops'']','Shake, strain, up, garnish.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (503,'Grim Reaper','[''kahlua'', ''151 proof rum'', ''grenadine'']','[''1 oz'', ''1 oz bacardi'', ''1 dash'']','Mix Kahlua and 151 in glass. Quickly add ice and pour grenadine over ice to give ice red tint.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (504,'Grizzly Bear','[''amaretto'', ''jägermeister'', ''kahlua'', ''milk'']','[''1 part'', ''1 part'', ''1 part'', ''2 1/2 parts'']','Served over ice. Sounds nasty, but tastes great.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (505,'Grown-Up Shirley Temple','[''luxardo cherry syrup (from jar of cherries)'', ''fresh lemon juice (optional)'', ''soda water'']','[''1 oz'', ''.25 oz'', ''6 oz'']','Fill a highball glass with ice, and build the syrup, lemon juice, and soda water. Stir, and garnish with a Luxardo Maraschino Cherry.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (506,'H.D.','[''whisky'', "bailey''s irish cream", ''coffee'']','[''4 cl'', ''8 cl'', '''']','Mix the whisky and Baileys Cream in a beer-glass (at least 50 cl). Fill the rest of the glass with coffee.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (507,'Hanky Panky (Classic)','[''no.3 london dry gin'', ''tempus fugit alessio vermouth di torino rosso'', ''luxardo fernet'']','[''1.5 oz'', ''1.5 oz'', ''2 dash'']','Shake ingredients with ice then strain into a chilled cocktail glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (508,'Hanny''s Hot Apple Toddy','[''luxardo amaretto di saschira'', ''apple cider'', ''heavy cream'', ''cinnamon'']','[''1.5 oz'', ''8 oz'', ''oz'', ''oz'']','Fill a 10-oz mug with boiling water. When the mug is hot, pour out the water and add the amaretto and cider. Top with a layer of cream. Grate fresh cinnamon on top as a garnish.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (509,'Happiness','[''jasmine tea/rice-infused junipero gin*'', ''leopold bros. sour apple liqueur'', ''lime'', ''house-made honey syrup**'', ''lo-fi gentian amaro'']','[''1.5 oz'', ''.75 oz'', ''.75 oz'', ''.5 oz'', ''.25 oz'']','Add all ingredients in a cocktail shaker, shake, fine strain into a chilled coupe or martini glass. Garnish with edible flower.. . *Jasmine Tea/Rice-Infused Junipero Gin:. -- 1 bottle (750ml) Junípero gin. -- .5 cup Jasmine Tea. -- .5 cup Jasmine Rice (wash rice to eliminate starch). . Sous Vide Version: Wash the jasmine rice to clean out the starch. Fill container with hot water. Set sous vide at 52c. Add vacuum seal bag with jasmine tea/rice in bath. Let sit in bath for 45 minutes. Take the bag out of bath and sit it in ice bath for 15 minutes. Open bag and fine strain back into bottle.. . Infusion Version: Wash the jasmine rice to clean out the starch. In a cambro/mason jar, add Junípero gin and jasmine tea and rice. Let infuse for 24 hours. Fine strain into bottle.. . **House-made Honey Syrup: . . Put equal parts of honey (10 oz) and water (10 oz) in a saucepan. Boil, then turn down to a simmer and stir until the honey is completely dissolved.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (510,'Happy Skipper','[''spiced rum'', ''ginger ale'', ''lime'', ''ice'']','[''1 1/2 cl'', '''', '''', '''']','Pour Captain Morgan''s Spiced Rum over ice, fill glass to top with Ginger Ale. Garnish with lime. Tastes like a cream soda. Named for the Gilligan''s Island reference ("The Captain" *in* "Ginger" is a Happy Skipper!)');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (511,'Harms'' Way','[''luxardo bitter bianco'', ''blanco tequila'', ''d.o.m benedictine'', ''bittermens xocolatl mole bitters'']','[''1 oz'', ''1.25 oz'', ''.25 oz'', ''1 dash'']','Pour all liquid ingredients into a mixing glass and stir about 55 times for proper dilution and optimal clarity. Strain into a chilled coupe. Twist fresh lemon peel over cocktail for aroma. Garnish with a simple lemon peel, trimmed neatly.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (512,'Harvey Wallbanger','[''vodka'', ''galliano'', ''orange juice'']','[''1 oz'', ''1/2 oz'', ''4 oz'']','Stir the vodka and orange juice with ice in the glass, then float the Galliano on top. Garnish and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (513,'Havana Cocktail','[''light rum'', ''pineapple juice'', ''lemon juice'']','[''1 oz'', ''1 oz'', ''1 tsp'']','In a shaker half-filled with ice cubes, combine all of the ingredients. Shake well. Strain into a cocktail glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (514,'Hawaiian Cocktail','[''gin'', ''triple sec'', ''pineapple juice'']','[''2 oz'', ''1/2 oz'', ''1 tblsp'']','Shake all ingredients with ice, strain into a cocktail glass, and serve.');
