INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (516,'Heart Breaker','[''bonded rye'', ''luxardo amaro abano'', ''punt e mes'', ''angostura bitters'']','[''1.5 oz'', ''.5 oz'', ''.5 oz'', ''7 dash'']','Pour all ingredients into a mixing glass with ice. Stir to chill and dilute. Strain into a chilled cocktail glass. Express the oil from the twist over the drink and discard.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (517,'Hemingway Daiquiri','[''pink pigeon rum'', ''fresh lime juice'', ''fresh grapefruit juice'', ''luxardo maraschino liqueur'']','[''1.5 oz'', ''.75 oz'', ''.5 oz'', ''.5 oz'']','Shake with ice in a mixing tin and double strain into a chilled cocktail coupe. Garnish with a grapefruit twist.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (518,'Hemingway Special','[''rum'', ''grapefruit juice'', ''maraschino liqueur'', ''lime juice'']','[''12 parts'', ''8 parts'', ''3 parts'', ''3 parts'']','Pour all ingredients into a shaker with ice. Shake.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (519,'Herbal flame','[''hot damn'', ''tea'']','[''5 shots'', ''very sweet'']','Pour Hot Damn 100 in bottom of a jar or regular glass. Fill the rest of the glass with sweet tea. Stir with spoon, straw, or better yet a cinnamon stick and leave it in.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (520,'Hibiscus Pisco Sour','[''barsol primero quebranta pisco'', ''lime juice'', ''hibiscus/rose syrup'', ''egg white'']','[''2 oz'', ''.75 oz'', ''.75 oz'', ''1'']','Dry shake, add ice and shake again. Strain into a coupe glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (521,'Hidarite','[''nikka coffey grain whiskey'', ''luxardo bitter'', ''tempus fugit alessio vermouth di torino rosso'', ''chocolate bitters'']','[''1.5 oz'', ''.75 oz'', ''.75 oz'', ''2 dash'']','Chilled. Serve up or on the rocks.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (522,'Highland Fling Cocktail','[''scotch'', ''sweet vermouth'', ''orange bitters'', ''olive'']','[''1 1/2 oz'', ''3/4 oz'', ''2 dashes'', ''1'']','Stir all ingredients (except olive) with ice and strain into a cocktail glass. Add the olive and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (523,'Hippi Hill','[''hophead vodka'', "king''s ginger liqueur", ''lime juice'', ''cranberry juice'']','[''1.5 oz'', ''.75 oz'', ''.5 oz'', ''.5 oz'']','Shake, fine strain into chilled coupe. Garnish with lime wheel.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (524,'Holland House','[''genever'', ''luxardo maraschino liqueur'', ''dry vermouth'', ''lemon juice'', ''pineapple gomme syrup'']','[''1 oz'', ''1 oz'', ''.5 oz'', ''.5 oz'', ''.5 oz'']','Shake. Serve up. Garnish with lemon peel/pineapple.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (525,'Holloween Punch','[''grape juice'', ''carbonated soft drink'', ''sherbet'', ''sherbet'']','['''', '', orange'', '''', '''']','Take a bunch of grape juice and a bunch of fizzy stuff (club soda, ginger ale, lemonlime, whatever). Mix them in a punch bowl. Take orange sherbet and lime sherbet. Scoop out scoops and float them in the punch, let them melt a little so that a nasty film spreads all over the top of the punch but there are still "bubbles" in it in the form of sherbet scoops. Looks horrible, tastes just fine.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (526,'Homemade Kahlua','[''sugar'', ''corn syrup'', ''coffee'', ''vanilla extract'', ''water'', ''vodka'']','[''2 1/2 cups'', ''1 cup'', ''1 1/2 oz instant'', ''2 oz'', ''3 cups boiling'', ''1 fifth'']','Dissolve sugar in 2 cups of boiling water and add corn syrup. Dissolve the instant coffee in the remaining water. Pour syrup and coffee in a gallon jug. Let it cool. Add vodka and vanilla when cold. For the best result, let the mixture "mature" for 4-5 weeks.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (527,'Honey Basil Smash','[''english harbour rum 5 yo'', ''honey'', ''lime wedges'', ''basil leaves'', ''ginger ale'']','[''1.5 oz'', ''.25 oz'', ''3'', ''4'', ''top'']','In collins glass, gently muddle honey, lime and basil leaves. Add English Harbour rum and ice and stir. Top with ginger ale.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (528,'Hophead Dirty Martini','[''hophead vodka'', ''olive juice'', ''dry vermouth'']','[''4 oz'', ''.5 oz'', ''1 dash'']','Combine all ingredients into a mixing glass and add ice. Shake and strain into a martini glass. Garnish with an olive.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (529,'Hophead Old Fashioned','[''hophead vodka'', ''simple syrup'', ''angostura bitters'', ''fee brothers gin barrel aged bitters'']','[''2 oz'', ''.25 oz'', ''2 dash'', ''1 dash'']','Combine all ingredients into a mixing glass and stir until proper dilution. Strain into a chilled bucket glass over a large ice cube and garnish with a dukes lemon and dukes grapefruit zest..');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (530,'Hopped-Up Mary','[''hophead vodka'', ''tomato juice'', "demitri''s bloody mary seasoning", ''fresh lemon juice'']','[''2 oz'', ''6 oz'', ''1.5 tsp'', ''.5 oz'']','Combine all ingredients in a chilled mixing glass, rimmed with sea salt and stir. Add ice. Stir again to mix. Garnish with a cocktail shrimp, lemon zest, fresh ground pepper and a celery stalk.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (531,'Hopped-Up Mule','[''hophead vodka'', "king''s ginger liqueur", ''lime juice'', ''ginger beer'']','[''2 oz'', ''.5 oz'', ''.75 oz'', ''top'']','Combine Hophead Vodka, Kings Ginger, and lime juice in a Collins glass and fill with ice. Top with ginger beer. Stir. Garnish with a lime wheel.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (532,'Horse''s Neck','[''lemon peel'', ''brandy'', ''ginger ale'', ''bitters'']','[''1 long strip'', ''2 oz'', ''5 oz'', ''2 dashes'']','Pour brandy and ginger ale directly into highball glass with ice cubes. Stir gently. Garnish with lemon zest. If desired, add dashes of Angostura Bitter.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (533,'Hot Chocolate to Die for','[''chocolate'', ''butter'', ''vanilla extract'', ''half-and-half'', ''marshmallows'']','[''12 oz fine'', ''1 tsp'', ''1/2 tsp'', ''1 cup'', ''mini'']','Melt the chocolate, butter and vanilla in a double boiler. When just smooth stir in the cream.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (534,'Hot Creamy Bush','[''irish whiskey'', "bailey''s irish cream", ''coffee'']','[''1 shot'', ''3/4 shot'', ''6 oz hot'']','Combine all ingredients in glass');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (535,'Hotaling Old Tom Gin','[''old tom gin'', ''7-spice syrup*'', ''lemon juice'', ''fever-tree tonic'']','[''1.5 oz'', ''.5 oz'', ''.75 oz'', ''1.5 oz'']','Garnished with orange peel and star anise. Served over the rocks in a stemless wine glass.. . *7-Spice Syrup:. -- Pink Peppercorn. -- Star Anise. -- Sumac. -- Cinnamon. -- Nutmeg. -- Coriander. -- Cardamom');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (536,'Hotaling Seaside Shandy','[''genevieve gin'', ''lager'']','[''1 oz'', ''1 bottle'']','Genevieve is Hotaling''s interpretation of genever, an ancient and mysterious gin style. The most common way to consume genever has always been straight. If you are planning to set sail for the day, picnic or do something else to enjoy the great outdoors, we recommend throwing a bottle of Genevieve in the freezer the evening before your outing and moving it to the cooler day of. We sip it straight while enjoying a lager. Glassware is dealer''s choice, but cordial glasses are elegant and fun.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (537,'Hotaling White Manhattan','[''christmas spirit'', ''tempus fugit alessio vermouth bianco'', ''luxardo triplum (triple sec)'', "fee brother''s orange bitters"]','[''2 oz'', ''1 oz'', ''.25 oz'', ''1 dash'']','Combine ingredients in a mixing glass and stir with ice. Strain and serve up with an orange twist.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (538,'Huntsman','[''pink pigeon rum'', ''be brown sugar'']','[''2 oz'', ''1 c'']','Soak brown sugar cube in Angostura and Orange bitters. Muddle soaked sugar cube into a paste in a rocks glass. Pour 2 oz. Pink Pigeon. Add ice and stir. Garnish with an orange peel and Luxardo Cherry..');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (539,'I''m Only Happy when it Rains','[''old potrero straight rye whiskey'', ''hirsh pinot noir reduction'', ''luxardo sangue morlacco cherry liqueur'', ''lemon juice'']','[''1 1/2 oz'', ''3/4 oz'', ''1/4 oz'', ''3/4 oz'']','Shake, double strain into a Burgundy Glass.');
