INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (541,'IPA Mule','[''hophead vodka'', ''ginger liqueur'', ''lime juice'', ''pure cane simple syrup'', ''deschutes fresh squeezed ipa (from tap)'']','[''1.5 oz'', ''.5 oz'', ''.75 oz'', ''.5 oz'', ''2 count'']','Add all ingredients except beer into shaker tin, shake vigorously for 10 seconds. Add beer in shaker tin then double strain into tin cup. Add mint for garish.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (542,'Ice Pick #1','[''vodka'', ''iced tea'', ''lemon juice'']','[''1 1/2 oz'', ''6 oz'', ''to taste'']','Put Vodka in glass fill with iced tea. Stir in lemon to taste.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (543,'Iced Coffee','[''coffee'', ''sugar'', ''water'', ''milk'']','[''1/4 cup instant'', ''1/4 cup'', ''1/4 cup hot'', ''4 cups cold'']','Mix together until coffee and sugar is dissolved. Add milk. Shake well. Using a blender or milk shake maker produces a very foamy drink. Serve in coffee mug.');
