INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (549,'Improved Whisky Cocktail','[''nikka whisky taketsuru pure malt 12 yo'', ''luxardo maraschino liqueur'', ''pontarlier anis'']','[''2 oz'', ''1 dash'', ''1 dash'']','Stir and strain into a chilled coupe.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (550,'In HINEsight','[''h by hine'', ''luxardo bitter'', ''quady muscat wine'']','[''1.5 oz'', ''1 oz'', ''.5 oz'']','Stir and strain into an old fashioned glass. Large ice cube.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (551,'International Mistress','[''amaro nonino quintessentia'', ''lemon juice'', ''mezcal'', ''small hand foods orgeat'', ''soda water'']','[''1.5 oz'', ''.75 oz'', ''.5 oz'', ''.5 oz'', ''2 oz'']','Stir, fine strain into a collins glass over ice. Top with soda. Garnish with grapefruit peel.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (552,'Ipamena','[''lime'', ''brown sugar'', ''passion fruit juice'', ''ginger ale'', ''ice'']','[''½'', ''2 tsp'', ''4 cl'', ''top up with'', ''fill'']','Cut half a lime into pieces, place in a shaker, add the sugar and crush. Measure the passion fruit juice, add it to the shaker and fill up with ice cubes. Close the shaker and shake vigorously. Pour the liquid into a glass, top up with ginger ale, stir with a teaspoon and then garnish the rim of the glass with a slice of lime');
