INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (618,'Kool First Aid','[''151 proof rum'', ''kool-aid'']','[''2 oz light'', ''1/2 tsp tropical'']','Add Kool Aid to a double shot glass, and top with rum. Slam and shoot.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (619,'Kool-Aid Shot','[''vodka'', ''amaretto'', ''sloe gin'', ''triple sec'', ''cranberry juice'']','[''1 shot'', ''1 shot'', ''1 shot'', ''1 shot'', '''']','Pour into a large glass with ice and stir. Add a little cranberry juice to taste.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (620,'Kool-Aid Slammer','[''kool-aid'', ''vodka'']','[''1/2 oz grape'', ''1/2 oz'']','Fill half the shot glass with the kool-aid first. Then put a paper towel over the top of the glass and slowly pour in the vodka. If you do it right, you should be able to see that the two liquids are separated, with the vodka on top. Now slam it! The last thing you''ll taste is the kool-aid.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (621,'Kumquat Smash','[''luxardo triplum (triple sec)'', ''ypioca brasilizar cachaca prata classica'', ''luxardo sour cherry syrup (previously amarena)'', ''kumquats'', ''mango flavored rum'']','[''1 oz'', ''1 oz'', ''.5 oz'', ''5'', ''1 float'']','Muddle the fruit with syrup in a mixing glass. Add ice and shake vigorously. Pour all ingredients(do not strain) in a tumbler glass. Add a float of Mango flavored Rum.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (622,'Kurant Tea','[''absolut kurant'', ''tea'', ''sugar'']','[''4 cl'', ''turkish apple'', ''(if needed)'']','Pour Absolut Kurant into a comfortably big tea-cup. Add the not too hot(!) apple tea and, if you like, some sugar. Enjoy!');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (623,'L''Aviation','[''no.3 london dry gin'', ''luxardo maraschino liqueur'', ''tempus fugit liqueur de violettes'', ''fresh lemon juice'']','[''1.5 oz'', ''0.5 oz'', ''0.5 oz'', ''.75 oz'']','Place ingredients in a shaker with ice and shake hard for seven seconds. Double strain into a chilled martini glass and garnish with lemon peel.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (624,'L.E.S. Is More','["karlsson''s gold vodka", ''esprit de june'', ''fresh lemon juice'', ''simple syrup'']','[''1.5 oz'', ''.75 oz'', ''.5 oz'', ''.5 oz'']','Combine all liquid ingredients in a highball or Collins glass with ice.. Stir lightly and garnish with a lemon twist making sure to release all the essential oils out of the twist. Also make sure to rub the entire rim of the glass with the twist before dropping it into the cocktail.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (625,'La Batida Loca','[''ypioca brasilizar cachaca prata reserva especial'', ''sweetened condensed milk'', ''passion fruit (fresh or puree)'', ''pilon syrup*'']','[''2 oz'', ''1.5 oz'', ''1 oz'', ''.5 oz'']','Add all of the ingredients, except seeds, to a shaker with ice and shake well. Double strain onto fresh ice into a collins glass. Garnish with 1 spoon of passion fruit seeds.. . *Pilon Syrup:. Homemade 50/50 simple syrup using Pilon Sugar made from Mexican Sugar Cane.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (626,'La Capilla','[''mezcal'', ''pierre ferrand curaçao'', ''gordy’s cocktail pickle brine'', ''watermelon juice'', ''fresh grapefruit juice'', ''fresh lime juice'']','[''1.5 oz'', ''.75 oz'', ''.5 oz'', ''1 oz'', ''.5 oz'', ''.5 oz'']','Pour ingredients in a shaker tin, add ice and shake well. . . Fine strain the drink into a double rocks glass filled with ice..  . Balance a small watermelon triangle on top of the glass. Grate fresh lime zest on top using a microplane, and then sprinkle with Maldon salt.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (627,'La Chiara','[''luxardo bitter bianco'', ''barsol primero quebranta pisco'', ''fever-tree indian tonic'', ''luxardo maraschino liqueur'']','[''1 oz'', ''1 oz'', ''2 oz'', ''1 dash'']','Build spirits in a tall glass over clear ice. Top with 4 oz tonic and garnish with a lemon twist curled into a flower if you''re feeling fancy.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (628,'La Hops Vie','[''hophead vodka'', ''fresh lemon juice'', ''fresh grapefruit juice'', ''simple syrup'', ''barrel aged orange bitters'', ''basil leaves'']','[''2 oz'', ''.75 oz'', ''.75 oz'', ''.5 oz'', ''gin'', ''fresh'']','Combine all ingredients in a shaker and shake until proper dilution. Double strain into a chilled martini or coupe glass and garnish with 2 fresh basil leaves that have been lightly "spanked" to release aromatics.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (629,'La La Land','["karlsson''s gold vodka", ''aperol'', ''raspberry/white pepper syrup'', ''fresh lemon juice'', ''fresh cucumber juice'', ''fresh mint'', ''s celery bitters'']','[''1.5 oz'', ''.5 oz'', ''.5 oz'', ''.5 oz'', ''.5 oz'', ''4-5 pc'', ''2 dash'']','In a mixing glass muddle mint, add all other ingredients and ice, shake, fine strain into a double old fashioned glass over fresh cracked ice. Garnish with cucumber slices and raspberries.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (630,'La Madrugada','[''barsol perfecto amor (aperitif wine)'', ''lemon juice'', ''luxardo amaro abano'', ''luxardo maraschino liqueur'']','[''1.5 oz'', ''.5 oz'', ''.5 oz'', ''.25 oz'']','Combine all ingredients in a mixing glass and add ice. Shake well and double strain and serve into a coupe.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (631,'La Maracuja','[''ypioca brasilizar cachaca prata reserva especial'', ''fresh passion fruit'', ''condense milk'', ''brown sugar syrup (2:1)'']','[''2 oz'', ''1.5 oz'', ''1 oz'', ''.25 oz'']','Add all ingredients to blender and blend throughly. Double strain into a glass with cracked ice garnish with a mint sprig and fresh grated nutmeg.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (632,'La Nueva Moda (The New Fashion)','[''chinaco anejo tequila'', ''mezcal'', ''luxardo maraschino liqueur'', ''agave nectar'', ''fee brothers aztec chocolate bitters'', ''bes sugar'']','[''2 oz'', ''.25 oz'', ''.25 oz'', ''2 drops'', ''3 dash'', ''2 c'']','In rocks glass, make an emulsion by using a glass muddler (wood muddlers absorb the ingredients too much). Place sugar cubes in glass. Add mezcal onto the cubes. Add bitters, onto cubes. Add drops of agave nectar, onto cubes. Add Luxardo, onto cubes. Muddle well to make a "syrup" paying attention the blending of all ingredients. . Take the long orange peel and twist all the essential oils into glass. Leave peel in glass. . Place Gläce Luxury Ice G-Cubed in glass. Rotate Gläce G-Cubed so that it becomes coated and chills the "syrup" swirl around the inside of glass. Pour the Anejo Tequila over Gläce G-Cubed. Stir with glass swizzle. Garnish with 3 Luxardo Cherries, speared..');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (633,'La Perle de Verre','[''luxardo bitter bianco'', ''citadelle gin'', ''orange bitters'', ''rose poached pear syrup*'', ''topo chico mineral water'', ''fever-tree sparkling lemon'']','[''1 oz'', ''1 oz'', ''2 dash'', ''.25 oz'', ''1 oz'', ''1 oz'']','Combine all liquids with exception of sodas, sir together. Strain into collins glass with singular large cube. Top with sodas. Garnish with sliver of poached pear from syrup (kept chilled).. . *Rose Poached Pear Syrup:. -- 1.5 C Rose (La Vielle Ferme or similar). -- 1.5 C Water . -- 2 C Sugar. -- 1 Large Lemon. -- 1 Half Orange. -- 1 tsp Ground Ginger (not fresh). -- 1 tsp Vanilla. -- 3-5 Whole Cloves. . Combine Rose (La Vielle Ferme or similar), water, and sugar. Bring to a simmer. Add zest of lemon and an orange, ground ginger, vanilla, and cloves. Peel and halve pears, set gently into liquid. Keep just under a simmer for 15 mins. Remove from heat, let cool at least 20 mins. Pears will keep for 3 days refrigerated in syrup. The syrup itself will last longer.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (634,'Lady Love Fizz','[''gin'', ''light cream'', ''powdered sugar'', ''lemon'', ''egg white'', ''carbonated water'']','[''2 oz'', ''2 tsp'', ''1 tsp'', ''juice of 1/2'', ''1'', '''']','Shake all ingredients (except carbonated water) with ice and strain into a cocktail glass over two ice cubes. Fill with carbonated water, stir, and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (635,'Land of Valencia','[''luxardo bitter bianco'', ''bols genever'', ''clarified coconut sugar blood orange cordial*'', ''copper & kings absinthe'']','[''.75 oz'', ''1.5 oz'', ''.5 oz'', ''1 dash'']','Spray vessel with absinthe as a rinse. Build cocktail in a stirring glass. Stir cocktail. Place a large format cube in the vessel. Pour cocktail over large format cube. . . To garnish, peel a blood orange skin and express the oils over the cocktail. Grab mint sprigs and slap over the top of hand to express oils, wrap the peel at the base of the mint sprigs, tie off with twine and set on top of large format cube.. . *Clarified Coconut Sugar Blood Orange Cordial:. -- Blood Oranges. -- Agar Agar. -- Coconut Sugar. . Juice blood oranges. Bring 1/4 of juice to a boil with agar agar. Pour cooked juice into the remaining 3/4 juice. Freeze the juice. Pull the block from the freezer and let drip through cheese cloth and perforated pan creating the clarified juice. Bring the juice together with a rich (2:1) coconut sugar creating a coconut sugar blood orange cordial.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (636,'Lassi - A South Indian Drink','[''yoghurt'', ''water'', ''cumin seed'', ''salt'', ''mint'']','[''1/2 cup plain'', ''1 1/4 cup cold'', ''1/2 tsp ground roasted'', ''1/4 tsp'', ''1/4 tsp dried'']','Blend in a blender for 3 seconds. Lassi is one of the easiest things to make, and there are many ways of making it. Basically, it is buttermilk (yoghurt whisked with water), and you can choose almost any consistency that you like, from the thinnest to the thickest. Serve cold.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (637,'Lassi - Mango','[''mango'', ''yoghurt'', ''sugar'', ''water'']','[''2'', ''2 cups'', ''1/2 cup'', ''1 cup iced'']','Put it all in a blender and pour over crushed ice. You can also use other fruits like strawberries and bananas.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (638,'Lassi - Sweet','[''yoghurt'', ''water'', ''sugar'', ''salt'', ''lemon juice'']','[''1 cup'', ''2 cups cold'', ''4 tblsp'', ''pinch'', ''2 tblsp'']','Put all ingredients into a blender and blend until nice and frothy. Serve chilled.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (639,'Lassi Khara','[''yoghurt'', ''water'', ''salt'', ''asafoetida'']','[''1 cup'', ''2 cups cold'', ''1 tsp'', ''1 pinch'']','Blend (frappe) in blender until frothy. Add torn curry leaves and serve cold.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (640,'Lassi Raita','[''yoghurt'', ''ice'', ''sugar'', ''lime'', ''salt'']','[''2 cups'', ''6-apr'', '''', '''', '''']','Blend the yoghurt and ice cubes together, until the yoghurt becomes more liquid. Add sugar to taste. The lemon/lime is optional but it gives it a slightly tart taste. Dash of salt. Raita is also good for the summer. Instead of having a traditional salad you can make raita instead.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (641,'Last Samurai','[''vodka'', ''gin'', ''luxardo maraschino liqueur'', ''cointreau'']','[''1 oz'', ''1 oz'', ''1 oz'', ''.5 oz'']','Shake. Serve up. Garnish with lime wheel.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (642,'Last Word','[''luxardo maraschino liqueur'', ''junipero gin'', ''green chartreuse'', ''fresh lime juice'']','[''.75 oz'', ''.75 oz'', ''.75 oz'', ''.75 oz'']','Shake all ingredients with ice then strain into a chilled cocktail glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (643,'Le Femme Nikita','[''nikka whisky taketsuru pure malt 12 yo'', ''rich cane syrup'', ''bittermens xocolatl mole bitters'', ''cinnamon bitters'']','[''2 oz'', ''.25 oz'', ''2 dash'', ''2 dash'']','Build in rocks glass. Use cylinder ice cube.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (644,'Le Sang et Sable','[''h by hine'', ''dubonnet rouge'', ''cherry herring'', ''blood orange juice'', ''lemon juice'']','[''1.5 oz'', ''.5 oz'', ''.5 oz'', ''.375 oz'', ''.375 oz'']','Add all ingredients to a shaker and fill with ice. Shake vigorously and strain into chilled stemmed cordial glass. Garnish with cocktail cherry and lemon twist.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (645,'Lemon Drop','[''absolut vodka'', ''cointreau'', ''lemon'']','[''1 1/2 shot'', ''1 1/2 shot'', ''juice of 1 wedge'']','Shake and strain into a chilled cocktail glass rimmed with sugar.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (646,'Lemon Shot','[''galliano'', ''absolut citron'', ''lemon'', ''sugar'', ''151 proof rum'']','[''1/2 oz'', ''1/2 oz'', ''wedge'', ''bacardi'', '''']','Mix Galliano and Absolut Citron in a shot glass, lay lemon wedge sprinkled with sugar over glass and pour a rum over wedge and glass. light rum with a lighter and let burn for a second. Do shot quickly and suck on lemon. If it is done correctly, this will taste like a shot of sweet lemonade.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (647,'Lemouroudji','[''ginger'', ''water'', ''lemon'', ''sugar'', ''cayenne pepper'']','[''2 pieces'', ''1 gal'', ''1 lb'', ''1 cup'', ''ground'']','Juice the lemons. Peel and grate the ginger. Place the grated ginger and a liberal dash of the cayenne pepper into a piece of cheesecloth, and tie it into a knot. Let soak in the water. After 15 minutes or so, add the sugar, and the lemon juice. Chill, and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (648,'Limeade','[''lime'', ''sugar'', ''soda water'', ''lime peel'']','[''juice of 1'', ''1 tblsp'', ''(seltzer water)'', '''']','In a large glass, put the lime juice and sugar, and stir well. Add cold seltzer water to fill. Put the lime peels in the glass. Drink. Repeat until limes or soda run out.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (649,'Limona Corona','[''corona'', ''bacardi limon'']','[''1 bottle'', ''1 oz'']','Open the Corona. Fill the empty space in the neck in the bottle with the rum. The bottle should be filled to the top. Plug the bottle with your thumb or the palm of your hand. Turn the bottle upside-down so the rum and beer mix. Turn the bottle rightside-up, unplug, and drink.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (650,'Little Ground Tapper','[''h by hine'', ''small hand foods pineapple gum syrup'', ''luxardo maraschino liqueur'', ''angostura bitters'']','[''2 oz'', ''.25 oz'', ''1 bsp'', ''2 dash'']','Build in an old fashioned glass over ice. Garnish with orange peel.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (651,'Local Edition Punch','[''h by hine'', ''t liqueur'', "king''s ginger liqueur", ''luxardo aperitivo'', '''']','[''12 bottle'', ''4 bottles luxardo apric'', ''1 bottle'', ''1 bottle'', ''4 bottles lemon juic'']','Combine all ingredients in a punch bowl. Mix well.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (652,'Loch Lomond','[''scotch'', ''drambuie'', ''dry vermouth'', ''lemon peel'']','[''2 oz'', ''1/2 oz'', ''1/2 oz'', ''1 twist of'']','In a mixing glass half-filled with ice cubes, combine the Scotch, Drambuie, and vermouth. Stir well. Strain into a cocktail glass. Garnish with the lemon twist.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (653,'London Town','[''gin'', ''maraschino liqueur'', ''orange bitters'']','[''1 1/2 oz'', ''1/2 oz'', ''2 dashes'']','In a mixing glass half-filled with ice cubes, combine all of the ingredients. Stir well. Strain into a cocktail glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (654,'Lone Tree Cocktail','[''sweet vermouth'', ''gin'']','[''3/4 oz'', ''1 1/2 oz'']','Stir ingredients with ice, strain into a cocktail glass, and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (655,'Lone Tree Cooler','[''carbonated water'', ''gin'', ''dry vermouth'', ''powdered sugar'', ''orange spiral'', ''lemon peel'']','['''', '''', '''', '''', '''', '''']','Stir powdered sugar and 2 oz. carbonated water in a collins glass. Fill glass with ice, add gin and vermouth, and stir. Fill with carbonated water and stir again. Add the twist of lemon peel and the orange spiral so that the end dangles over rim of glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (656,'Lonesome George','[''apricot-infused h by hine'', ''napoleon amontillado'', ''mandarine napoléon'', ''lazzaroni amaro'']','[''1.25 oz'', ''1 oz'', ''.25 oz'', ''.5 oz'']','Stir with Ice and strain into a small wine glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (657,'Long Division','[''tequila'', ''luxardo maraschino liqueur'', ''aperol'', ''lime juice'']','[''1.5 oz'', ''1 oz'', ''.5 oz'', ''.25 oz'']','Shake. Serve up. Top with sparkling rose. Garnish with lime peel.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (658,'Long Island Iced Tea','[''vodka'', ''tequila'', ''light rum'', ''gin'', ''coca-cola'', ''lemon peel'']','[''1/2 oz'', ''1/2 oz'', ''1/2 oz'', ''1/2 oz'', ''1 dash'', ''twist of'']','Mix all contents in a highball glass and sitr gently. Add dash of Coca-Cola for the coloring and garnish with lemon or lime twist.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (659,'Long Island Limoncello Martini','["hangar 1 buddha''s hand citron vodka", ''luxardo limoncello'', ''fresh lemon juice'', ''monin cane syrup'']','[''1 oz'', ''1 oz'', ''.75 oz'', ''.25 oz'']','Served up and finished with a lemon wheel.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (660,'Long Island Tea','[''vodka'', ''light rum'', ''gin'', ''tequila'', ''lemon'', ''coca-cola'']','[''1/2 oz'', ''1/2 oz'', ''1/2 oz'', ''1/2 oz'', ''juice of 1/2'', ''1 splash'']','Combine all ingredients (except cola) and pour over ice in a highball glass. Add the splash of cola for color. Decorate with a slice of lemon and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (661,'Long vodka','[''vodka'', ''lime'', ''angostura bitters'', ''tonic water'', ''ice'']','[''5 cl'', ''2-jan'', ''4 dashes'', ''1 dl schweppes'', ''4'']','Shake a tall glass with ice cubes and Angostura, coating the inside of the glass. Por the vodka onto this, add 1 slice of lime and squeeze juice out of remainder, mix with tonic, stir and voila you have a Long Vodka');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (662,'Lord And Lady','[''dark rum'', ''tia maria'']','[''1 1/2 oz'', ''1/2 oz'']','Pour the rum and Tia Maria into an old-fashioned glass almost filled with ice cubes. Stir well.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (663,'Lost at Sea','["king''s ginger liqueur", ''lime juice'', ''cane sugar syrup'', ''club soda'']','[''2 oz'', ''1 oz'', ''.5 oz'', ''2 oz'']','Muddle basil in mixing glass, add all ingredients except club soda. Shake and strain into a collins glass, top with club soda.  Top with crushed ice and garnish with a basil sprig.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (664,'Lovage In The Garden','[''hophead vodka'', ''sun gold cherry tomatoes'', ''lovage leaves'', ''lime juice'', ''agave syrup (1:1 agave to water)'', ''luxardo aperitivo'', ''hepps ghost pepper salt'']','[''2 oz'', ''3'', ''4'', ''.75 oz'', ''.5 oz'', ''.5 oz'', ''1 pinc'']','Muddle everything in a tin except Hophead. Then add Hophead and ice and shake and strain into a rocks glass with ice and garnish with a lovage sprig.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (665,'Lox, Stock & Two Smoking Barrels','[''vintage reserve'', ''lustau east india solera'', ''syrup'', ''milk'']','[''glenrothes'', ''coffee-infused'', ''cinnamon'', ''mil'']',NULL);
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (666,'Lucky No.3','[''no.3 london dry gin'', ''luxardo aperitivo'', ''tempus fugit alessio vermouth di torino rosso'', ''grapefruit bitters'', ''kosher salt'']','[''1.5 oz'', ''1 oz'', ''.75 oz'', ''1 dash'', ''1 pinc'']',NULL);
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (667,'Lunch Box','[''beer'', ''amaretto'', ''orange juice'']','[''3/4 bottle'', ''1 shot'', ''1 oz'']','Fill a pint glass almost full with beer. Then fill the rest with orange juice (careful not to fill it to the top). Then take the shot of Amaretto and drop it in.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (668,'Lux Cherry Sparkler','[''luxardo sangue morlacco cherry liqueur'', ''prosecco'']','[''1 oz'', ''4 oz'']','Add the Cherry Liqueur to a flute, top with Prosecco, and garnish with a Luxardo Maraschino Cherry.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (669,'Lux Japanese Lemonade','[''no.3 london dry gin'', ''jummai ginjo sake'', ''luxardo limoncello'', ''dill cordial'', ''meyer lemon juice'', ''yuzu juice'', ''fresh egg white'']','[''1.5 oz'', ''.5 oz'', ''.25 oz'', ''.25 oz'', ''.5 oz'', ''.25 oz'', ''.5 oz'']','Dry Shake, add ice and shake for 10 seconds. Double strain into a Copita Glass. Garnish with Dill.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (670,'Lux Orchard Lemonade','[''luxardo maraschino liqueur'', ''junipero gin'', ''monin apple puree'', ''lemon juice'', ''fennel bitters'']','[''.75 oz'', ''.5 oz'', ''.75 oz'', ''.25 oz'', ''1 dash'']','Add all ingredients in a mixing glass with fresh ice. Shake vigorously and strain over fresh ice. Garnish with Luxardo Cherry.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (671,'Lux Shandy','[''luxardo aperitivo'', ''lemon juice'', ''orange juice'', ''simple syrup'', ''anchor california lager'']','[''1 oz'', ''.5 oz'', ''.5 oz'', ''.5 oz'', ''top'']','Add all ingredients to a mixing glass. Add ice and integration shake. Pour over fresh ice into a highball and top with California Lager. Garnish with a half orange wheel.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (672,'Luxardo Fresco','[''luxardo maraschino liqueur'', ''mber'', ''lemon juice'', ''tonic'']','[''1.5 oz'', ''1 strip cuc'', ''1 dash'', ''top'']','Quick stir and garnish with rosemary.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (673,'Luxardo Ice Cream Float','[''luxardo cherry syrup (from jar of cherries)'', ''op vanilla ice cream'', ''soda water'']','[''1 oz'', ''1 sc'', ''top'']','Build syrup and ice cream in a lowball cocktail glass, and top with soda water. Serve with a metal straw, a spoon, and garnish with a Luxardo Maraschino Cherry.');
