INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (744,'National Aquarium','[''rum'', ''vodka'', ''gin'', ''blue curacao'', ''sour mix'', ''lemon-lime soda'']','[''1/2 oz'', ''1/2 oz'', ''1/2 oz'', ''1/2 oz'', ''2 oz'', ''1 splash'']','Pour all ingredients into a shaker of ice. Shake well. Serve on the rocks.');
