INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (750,'Neighborhood Negroni','[''junipero gin'', ''campari'', ''tempus fugit alessio vermouth di torino rosso'']','[''1 oz'', ''1 oz'', ''1 oz'']','Add all the ingredients to a mixing glass and fill with ice. Stir until well chilled and strain into a chilled cocktail glass. Garnish with a flamed orange twist.');
