INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (752,'Nellie Fer'' Toddy','[''blended scotch'', "king''s ginger liqueur", ''the genuine curacao liqueur orange'', ''fresh lemon juice'', ''hot water'', "peychaud''s bitters"]','[''1 oz'', ''1 oz'', ''.5 oz'', ''.75 oz'', ''top'', ''1 dash'']','Build then pour into a pre-heated Irish coffee glass or mug. Top with hot water. Garnish with the orange slice and add a dash of Peychaud''s Bitters.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (753,'New Beginnings','[''luxardo bitter bianco'', ''avua cachaca prata'', "broker''s london dry gin", ''coconut water'', ''toasted coconut tincture*'', ''almond extract'', ''fever-tree mediterranean tonic water'']','[''.75 oz'', ''.75 oz'', ''.75 oz'', ''.75 oz'', ''2 dash'', ''1 dash'', ''top'']','Combine Luxardo, cachaca, gin, coconut water, tincture and almond extract in a yari (mixing glass). Add ice. Stir for proper dilution (30-40 revolutions). Pour in collins glass over a clarified ice sphere. Top cocktail with Fever-Tree. Add 3 almond slices for garnish.. . *Toasted Coconut Tincture:. -- 4 oz (by weight) Semisweet Coconut Flakes. -- High-Proof Neutral Grain Spirit (preferably Everclear). . Lightly toast semisweet coconut flakes. Put in a small mason jar. Fill jar with high-proof neutral grain spirit. Let sit for 3 days then strain off.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (754,'New York Lemonade','[''absolut citron'', ''grand marnier'', ''lemon juice'', ''club soda'']','[''2 oz'', ''1 oz'', ''2 oz sweetened'', ''1 oz'']','Serve in a chilled cocktail glass. Lemon and sugar the rim. Stir and Strain.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (755,'New York Sour','[''blended whiskey'', ''lemon'', ''sugar'', ''red wine'', ''lemon'', ''cherry'']','[''2 oz'', ''juice of 1/2'', ''1 tsp'', ''(claret)'', '''', '''']','Shake blended whiskey, juice of lemon, and powdered sugar with ice and strain into a whiskey sour glass. Float claret on top. Decorate with the half-slice of lemon and the cherry and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (756,'Newton''s Theory','[''nikka whisky taketsuru pure malt 12 yo'', ''c. drouin pommeau de normandie'', ''luxardo maraschino liqueur'', ''black walnut bitters'']','[''2 oz'', ''.75 oz'', ''.25 oz'', ''4 dash'']','Combine all ingredients into a mixing glass and add ice. Stir for about 10 seconds and then strain into a chilled cocktail coupe. Garnish with skewered apple spheres and be thankful you''re having a cocktail, and not having an apple shot off your head.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (757,'Nick & Janae','[''h by hine'', ''luxardo amaro abano'', ''fresh lemon juice'', ''strawberry syrup*'', ''egg white'']','[''1.5 oz'', ''.5 oz'', ''.75 oz'', ''.75 oz'', ''.5 oz'']','Add cognac, amaro, lemon, strawberry syrup and fresh cracked egg white of a whole egg to tin. Dry shake without ice approx. 5 seconds to emulsify. Add ice and shake hard again approx. 10 seconds. Fine strain into coupe. Crack drag black pepper over surface. Serve and enjoy.. . *Strawberry syrup- 1 cup fresh strawberries 1 cup water 1cup sugar. Blend in vitamix then heat on stove and stir until hot to bind sugars, don''t boil. Bottle and refrigerate.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (758,'Nieux Carré','["karlsson''s gold vodka", ''gran duque d alba solera gran reserva brandy'', ''carpano antica vermouth'', ''benedictine'', "ure peychaud''s bitters", ''ure angostura bitters'']','[''1.5 oz'', ''.75 oz'', ''.75 oz'', ''.25 oz'', ''1 tinc'', ''1 tinc'']','Combine all liquid ingredients in a chilled mixing glass with cracked ice and stir for 20 seconds.. Double strain the cocktail over a large cube placed in an old-fashioned glass.. Garnish with a lemon twist making sure to release all the essential oils out of the twist. Also make sure to rub the entire rim of the glass with the twist before dropping it into the cocktail.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (759,'Nigamono','[''nikka coffey grain whisky'', ''maurin quina'', ''luxardo amaro abano'']','[''2 oz'', ''.75 oz'', ''.75 oz'']','Stir. Use large ice cube.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (760,'Nikka Coffey Grain Perfect Manhattan','[''nikka coffey grain whisky'', ''tempus fugit alessio vermouth di torino rosso'', ''dolin vermouth'', "tempus fugit abbott''s bitters"]','[''2 oz'', ''.5 oz'', ''.5 oz'', ''2 dash'']','In mixing glass or cocktail shaker filled with ice, combine whiskey, vermouth, and bitters. Stir well, about 20 seconds, then strain into cocktail glass. Add cherry and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (761,'Nikka Coffey Malt Old Fashioned','[''nikka coffey malt whisky'', ''demerara syrup'', "tempus fugit abbott''s bitters"]','[''2.5 oz'', ''.5 oz'', ''4 dash'']','In an old fashioned glass, muddle the lemon peel with the bitters and demerara syrup, just enough to bruise the peel and release its oils. Add Coffey Malt and ice, and stir for 20 to 30 seconds, or until well chilled.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (762,'Nikka Earl Grey','[''nikka whisky taketsuru pure malt 12 yo'', ''honey'', ''earl grey syrup'', ''malt'']','[''2.5 oz'', ''.5 oz'', ''.5 oz'', ''smoked'']','Stir over ice and serve in a rocks glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (763,'Nikka''s Golden Cherry','[''nikka coffey grain whisky'', ''luxardo maraschino liqueur'', ''dry vermouth infusion*'', ''bittermens burlesque bitters'', ''lemon foam**'']','[''2 oz'', ''.5 oz'', ''.75 oz'', ''2 dash'', ''top'']','Stir ingredients over cracked ice. With a julep strainer, pour into a chilled cocktail glass. Once poured, top with lemon foam.. . *Dry Vermouth Infusion:. -- 1.5 oz Togarashi Spice. -- 3 Vanilla Beans. -- 2 bags of Green Tea. Place ingredients in cheesecloth to steep in a bell jar with 1 bottle of Dolin Dry vermouth for 2 days. . Remove the infusion elements and refrigerate the infusion.. . **Lemon Foam:. -- 1 bsp Lemon Juice. -- 1 bsp Simple Syrup. -- 1 Egg White . Place ingredients into a shaker tin and whip with a bar whisk into a foam.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (764,'No.10','[''woodford reserve double oaked'', ''luxardo bitter'', ''rothman & winter orchard apricot liqueur'', ''luxardo maraschino liqueur'']','[''1.5 oz'', ''.75 oz'', ''.75 oz'', ''.25 oz'']',NULL);
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (765,'No.3 & T','[''no.3 london dry gin'', ''lemon lemon'', ''water'']','[''1.5 oz'', ''1'', ''1 fill tonic'']','Pour No.3 gin into a highball glass over ice cubes. Add a slice of lemon and stir. Top up with tonic water to taste.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (766,'No.3 Bramble','[''no.3 london dry gin'', ''fresh lemon juice'', ''briottet creme de mure (blackberry)'', ''pages elderflower syrup'', ''angostura bitters (optional)'']','[''1.5 oz'', ''.5 oz'', ''.5 oz'', ''.25 oz'', ''1 dash'']','Shake all ingredients well in cocktail shaker. Strain into ice filled rock glass. Top with soda.');
