INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (769,'Nola ''75','[''junipero gin'', ''pages elderflower syrup'', ''fresh lemon juice'', ''champagne (sparkling wine)'']','[''1.25 oz'', ''.5 oz'', ''.5 oz'', ''oz'']','Shake first three ingredients in an ice-filled cocktail shaker. Strain into a chilled serving glass. Top with chilled champagne, or sparkling wine.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (770,'Nori & Islay','[''junipero gin'', ''nori-infused sake*'', ''celery bitters'', ''islay scotch'']','[''1.5 oz'', ''1.5 oz'', ''1 dash'', ''3 dash'']','Combine all ingredients in a mixing glass, stir, and strain into a chilled Nick & Nora glass. Garnish with a picked cucumber ribbon.. . *Nori-Infused Sake: . -- Ginjo Sake. -- Nori Seaweed Sheet. . Using an Oji Style Cold Brew Coffee Maker, drip Ginjo sake through a 0.5 gram of nori seaweed sheet that has been hand-shredded. It takes 3 hours to infuse a 750 ml bottle.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (771,'Northern Hemisphere (Nikka 12 Edition)','[''nikka whisky taketsuru pure malt 12 yo'', ''english harbour rum'', ''the genuine curacao liqueur clear'', ''lemon juice'', ''pineapple juice'', ''grenadine*'']','[''2 oz'', ''1 oz'', ''.75 oz'', ''.75 oz'', ''.75 oz'', ''.75 oz'']','Shake - pebble/crushed ice');
