INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (773,'Nuked Hot Chocolate','[''cocoa powder'', ''sugar'', ''vanilla extract'', ''milk'']','[''2 tsp'', ''1 tsp'', ''1/2 tsp'', ''12 oz'']','Mix with a bit of milk (1 oz or so) in coffee mug. Nuke mug for about 30-50 seconds. Stir until the heated cocoa dissolves. Fill mug with milk. Nuke for 1-2 minutes, depending on wattage and preferences as to burnt mouth parts.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (774,'Nutty Irishman','["bailey''s irish cream", ''frangelico'', ''milk'']','[''1 part'', ''1 part'', ''1 part'']','Serve over ice');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (775,'OH Snap!','[''junipero gin'', ''house-made simple syrup*'', ''manzanilla sherry'', ''lemon juice'', ''absinthe'', ''sugar snap peas'', ''tonic'']','[''1 oz'', ''1 oz'', ''1 oz'', ''.75 oz'', ''2 dash'', ''6-8'', ''top'']','Add 6-8 sugar snap peas to cocktail shaker. Lightly muddle the peas. Add all other ingredients except for the tonic. Shake and double strain into a collins glass. Top with tonic. . . *House-made Simple Syrup: . -- 1 cup Sugar. -- 1 cup Water. . Add equal parts sugar and water into a small saucepan, bring to a boil; simmer until the sugar is dissolved, approximately 3 minutes. Remove from heat, let cool to room temperature and transfer to a clean glass jar. Cover and keep refrigerated for up to 2 weeks.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (776,'Oaxacan Martini','[''mezcal'', ''mandarine napoleon'', ''lime juice'', ''castelvetrano olive brine'', ''fennel bitters'']','[''2 oz'', ''.5 oz'', ''.5 oz'', ''1 oz'', ''3 dash'']','Shake all ingredients for 10-15 seconds (over shaking will over dilute) and fine strain into chilled coupe glass. Garnish with Xoconostle (sour prickly pear) and a stuffed Castelvetrano olive.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (777,'Obituary Cocktail','[''mansinthe absinthe'', ''genevieve gin'', ''tempus fugit alessio extra dry vermouth'']','[''.25 oz'', ''2 oz'', ''.25 oz'']','Stir with ice. Strain into a cocktail glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (778,'Ode to Kinshi','[''nikka whisky yoichi single malt 15 yo'', ''cocchi americano rosa'', ''luxardo amaro abano'', ''bittermens xocolatl mole bitters'']','[''1.5 oz'', ''.75 oz'', ''.5 oz'', ''5 dash'']','Place all ingredients into a Yarai mixing glass with ice and stir. Strain into a coupe. Garnish with an apple slice.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (779,'Off the Radar','[''junipero gin'', ''lemon juice'', ''luxardo maraschino liqueur'', ''liqueur de violette'', ''tonic water'']','[''1.5 oz'', ''.5 oz'', ''.25 oz'', ''.25 oz'', ''3-4 oz'']','Add gin, lemon juice, maraschino liqueur, and liqueur de violette to a collins glass filled with ice. Add tonic water; stir to combine. Garnish with a lemon wheel.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (780,'Old Fashioned','[''bourbon'', ''angostura bitters'', ''sugar'', ''water'']','[''4.5 cl'', ''2 dashes'', ''1 cube'', ''dash'']','Place sugar cube in old fashioned glass and saturate with bitters, add a dash of plain water. Muddle until dissolved.
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (781,'Old Tom and The Sea','[''old tom gin'', ''tempus fugit alessio vermouth bianco'', ''luxardo sangue morlacco cherry liqueur'', ''lemon juice'']','[''1.5 oz'', ''1 oz'', ''.5 oz'', ''.5 oz'']','Combine the ingredients. Shake and strain over ice. Lemon and/or cherry flag garnish.. . Add soda water if you''d like to water it down.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (782,'On the Flipside','[''pink pigeon rum'', ''lime juice'', ''simple syrup'', ''mint leaves'', ''muddled'', ''mber slices'', ''muddled'']','[''1.5 oz'', ''1 oz'', ''.75 oz'', ''2'', ''muddle'', ''3  cuc'', ''muddle'']','Muddle 3-4 cucumber slices and a couple mint leaves. Shake and strain into martini or coupe glass. Garnish with lime or mint or cucumber.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (783,'On the Fritz','[''junipero gin'', ''chateau de montifaud pineau des charentes blanc'', ''lemon verbena cordial*'', ''lemon juice'', ''mixwell young ginger ale'']','[''1.5 oz'', ''.5 oz'', ''.75 oz'', ''.5 oz'', ''top'']','Shake all ingredients and double strain over fresh ice. Garnish with lemon verbena leaf.. . *Lemon Verbena Cordial:. -- Fresh Lemon Verbena . -- Fresh Mint. -- Grapefruit Peel . -- Lemon Peel. -- Pure Cane Sugar. -- Citric Acid. . Make a fresh herb and citrus peel tea and add sugar and citric acid to taste. Let cool before using.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (784,'On the Wings of Eagles','[''h by hine'', ''rye whiskey'', ''green chartreuse'', ''lemon bitters'', '''']','[''1.5 oz'', ''.75 oz'', ''.75 oz'', ''2 dash'', ''1 splash fernet-branc'']','Add cognac, rye, Chartreuse and bitters to a mixing glass with ice, and stir until cold. Add splash of fernet to a rocks glass, roll the glass to coat the sides, and discard the excess. Add one large ice cube to the glass, strain the drink over the cube, and garnish with a mint sprig.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (785,'Opaka Raka','[''junipero gin'', "don''s mix*", ''lime juice'', ''simple syrup'', "bittermens ''elemakule tiki bitters"]','[''1.5 oz'', ''1.5 oz'', ''.75 oz'', ''.25 oz'', ''1 dash'']','Shake and strain into an ice-filled highball glass. Garnish with a lime wheel.. . *Don''s Mix:. -- 2 parts Grapefruit Juice. -- 1 part Cinnamon Syrup');
