INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (788,'Orange Oasis','[''cherry brandy'', ''gin'', ''orange juice'', ''ginger ale'']','[''1/2 oz'', ''1 1/2 oz'', ''4 oz'', '''']','Shake brandy, gin, and orange juice with ice and strain into a highball glass over ice cubes. Fill with ginger ale, stir, and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (789,'Orange Push-up','[''spiced rum'', ''grenadine'', ''orange juice'', ''sour mix'']','[''1.5 oz'', ''0.5 oz'', ''4 oz'', ''1 splash'']','Combine liquors in a blender. Add a half scoop of ice and blend. Garnish with an orange and cherry flag. So good it will melt in your mouth!!!');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (790,'Orange Scented Hot Chocolate','[''milk'', ''chocolate'', ''orange peel'', ''espresso'', ''nutmeg'']','[''2 cups'', ''4 oz chopped bittersweet or semi-sweet'', ''3 2-inch strips'', ''1/2 tsp instant'', ''1/8 tsp ground'']','Combine all ingredients in heavy medium saucepan. Stir over low heat until chocolate melts. Increase heat and bring just to a boil, stirring often. Remove from heat and whisk untily frothy. Return to heat and bring to boil again. Remove from heat, whisk until frothy. Repeat heating and whisking once again. Discard orange peel. (Can be prepared 2 hours ahead. Let stand at room temperature. Before serving, bring just to boil, remove from heat and whisk until frothy.) Pour hot chocolate into coffee mugs. Makes 2 servings.');
