INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (816,'Pear and Cranberry Fizz','["karlsson''s gold vodka", ''fresh lemon juice'', ''rothman & winter orchard pear liqueur'', ''cranberry juice'', ''ure fee brothers cranberry bitters'', ''fresh cranberries'']','[''1.5 oz'', ''.5 oz'', ''1 oz'', ''.75 oz'', ''1 tinc'', ''10'']','In a Boston shaker combine and muddle the fresh lemon juice and cranberries. Add the remaining liquid ingredients except the club soda with hard ice and shake vigorously for 20 seconds pour directly into a highball add a little more ice to fill the glass and top with the club soda. Garnish with a few pear slices and fresh cranberries.');
