INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (917,'Sage & Sound','[''junipero gin'', ''white port'', ''lemon juice'', ''honey cinnamon syrup*'', ''whole egg'', ''muddled sage leaves'']','[''1.5 oz'', ''1 oz'', ''.75 oz'', ''.5 oz'', ''1'', ''8'']','Muddle the sage in a mixing tin. Add the remaining ingredients and dry shake (no ice) for 15 seconds. Add ice and shake another 15 seconds to chill down the drink. Double strain into a large coupe glass. Garnish with a whole star anise floated in the middle of the egg white.. . *Honey Cinnamon Syrup:. -- 16 oz Honey. -- 2 Cinnamon Sticks. -- 8 oz Water. Combine all the ingredients together in a pot. Place on the stove and bring to a simmer, stirring the honey so it doesn’t burn and let the pot simmer for 20 minutes. Remove from heat and place on an ice bath. Place in a container and label and date it. Store in the fridge for up to 2 weeks.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (918,'Sahara Glowing Heart','[''junipero gin'', ''apricot eau-de-vie'', ''grenadine'', ''lemon bitters'', ''absinthe'']','[''1.5 oz'', ''.75 oz'', ''.25 oz'', ''2 dash'', ''2 dash'']','Combine all ingredients in a mixing glass, add ice and then stir. Strain into a Nick & Nora glass and garnish with a lemon twist.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (919,'Salty Dog','[''grapefruit juice'', ''gin'', ''salt'']','[''5 oz'', ''1 1/2 oz'', ''1/4 tsp'']','Pour all ingredients over ice cubes in a highball glass. Stir well and serve. (Vodka may be substituted for gin, if preferred.)');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (920,'Salty Swede','["karlsson''s gold vodka", ''fresh grapefruit juice'', ''club soda'', ''ure grapefruit bitters'']','[''2 oz'', ''2 oz'', ''1 oz'', ''1 tinc'']','Combine the Karlsson''s Gold Vodka and grapefruit in a shaker with ice. Shake for 15 seconds and strain over fresh ice in an old-fashioned glass that has a salted rim. Top the cocktail with the club soda.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (921,'San Fran Shandy','[''hophead vodka'', "king''s ginger liqueur", "breckle''s brown ale", ''fresh lemon juice'', ''honey syrup'', ''egg white'', ''angostura bitters'']','[''2 oz'', ''.5 oz'', ''3 oz'', ''1 oz'', ''.5 oz'', ''.5 oz'', ''2 dash'']','Combine Hophead, lemon juice, King''s Ginger, honey syrup, egg white, and bitters in a shaker and dry shake to emulsify egg whites. Add ice and shake vigorously until proper dilution. Strain into a chilled fizz glass. Empty ice out of shakers and fill shaker with ale to create a froth. Pour this froth over the drink and garnish with half an orange wheel.. 	.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (922,'San Fran Soda','[''hophead vodka'', ''fresh lime juice'', ''soda water'', ''steam beer'']','[''1.25 oz'', ''.5 oz'', ''splash'', ''top'']','Pour over ice in a pint glass, then fill with steam beer. Garnish with a lemon wedge and a straw.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (923,'San Francisco','[''vodka'', ''creme de banane'', ''grenadine'', ''orange juice'']','[''2 cl'', ''2 cl'', '''', '''']','Take a tall glass and put in a few ice cubes, fill the vodka over it and fill with juice then the "creme", to end fill in the grenadine but very carefully at the side of the glass so it will lay down in the bottom. garnish with orange and strawberry.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (924,'San Francisco Harvest','[''junipero gin'', ''oloroso sherry'', ''lemon juice'', ''winter spice brown sugar syrup*'', ''pineapple juice'']','[''1.5 oz'', ''.75 oz'', ''.75 oz'', ''.75 oz'', ''1 oz'']','Combine ingredients in a shaker with ice. Shake and double strain into a coupe glass. Garnish with nutmeg.. . *Winter Spice Brown Sugar Syrup: . -- 2 qt Brown Sugar. -- 2 qt Water. -- 3 Tbsp Ground Ginger. -- 3 Tbsp Ground Nutmeg. -- 1 Tbsp Clove. . Heat and simmer the below ingredients in a medium pot for 15 minutes. Strain with cheesecloth.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (925,'San Francisco Strong (Skrong)','[''junipero gin'', "tempus fugit kina l''aero d''or", ''luxardo bitter bianco'', ''lemon juice'', ''egg whites'', ''rich simple syrup*'']','[''.75 oz'', ''.75 oz'', ''.75 oz'', ''.75 oz'', ''.75 oz'', ''.5 oz'']','Add all ingredients to tin and dry shake. Add ice and vigorously shake for 12 seconds. Fine strain into chilled cocktail glass. Garnish by sprinkling matcha powder (.25 tsp). . . *Rich Simple Syrup: . -- 2 cups Granulated Sugar. -- 1 cup Water. . Combine sugar and water in a small saucepan over medium heat and stir until sugar dissolves. Remove from heat, let cool to room temperature and transfer to a clean glass jar. Cover and keep refrigerated for up to 2 weeks.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (926,'Sangria #1','[''red wine'', ''sugar'', ''orange juice'', ''lemon juice'', ''cloves'', ''cinnamon'']','[''1 bottle'', ''1/2 cup'', ''1 cup'', ''1 cup'', '''', '''']','Mix all together in a pitcher and refrigerate. Add cloves and cinnamon sticks to taste. Serve in wine glasses.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (927,'Sangria - The World''s Best','[''red wine'', ''sugar'', ''lemon'', ''orange'', ''apple'', ''brandy'', ''soda water'']','[''1 1/2 l'', ''1 cup'', ''1 large'', ''1 large'', ''1 large'', ''3-4 oz plain'', '''']','Mix wine, sugar and fruit, and let sit in the fridge for 18-24 hours. The mixture will have a somewhat syrupy consistency. Before serving stir in brandy and cut the mixture with soda water until it have a thinner, more wine like consistency. Serve from a pitcher in wine glasses.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (928,'Santa Patricia','[''blanco tequila'', ''dry curacao'', ''luxardo bitter'', ''fresh ruby red grapefruit juice'', ''cinnamon syrup'']','[''1 oz'', ''.5 oz'', ''.5 oz'', ''.75 oz'', ''.5 oz'']','Shake all ingredients very well with ice and strain over ice into a highball glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (929,'Sazerac','[''ricard'', ''sugar'', ''peychaud bitters'', ''water'', ''bourbon'', ''lemon peel'']','[''1 tsp'', ''1/2 tsp superfine'', ''2 dashes'', ''1 tsp'', ''2 oz'', ''1 twist of'']','Rinse a chilled old-fashioned glass with the absinthe, add crushed ice, and set it aside. Stir the remaining ingredients over ice and set it aside. Discard the ice and any excess absinthe from the prepared glass, and strain the drink into the glass. Add the lemon peel for garnish.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (930,'Scooter','[''brandy'', ''amaretto'', ''light cream'']','[''1 oz'', ''1 oz'', ''1 oz'']','Shake all ingredients well with cracked ice, strain into a cocktail glass, and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (931,'Scotch Cobbler','[''scotch'', ''brandy'', ''curacao'', ''orange'', ''mint'']','[''2 oz'', ''4 dashes'', ''4 dashes'', ''1 slice'', ''1'']','Pour scotch, brandy, and curacao over ice in an old-fashioned glass. Add the orange slice, top with the mint sprig, and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (932,'Scotch Joplin','[''ardbeg ten'', ''luxardo aperitivo'', ''the genuine curacao liqueur orange'', ''orange bitters'']','[''2 oz'', ''.75 oz'', ''.5 oz'', ''5 dash'']','Stir, strain into a chilled Nick and Nora glass. Garnish with lemon twist.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (933,'Scotch Sour','[''scotch'', ''lime'', ''powdered sugar'', ''lemon'', ''cherry'']','[''1 1/2 oz'', ''juice of 1/2'', ''1/2 tsp'', ''1/2 slice'', ''1'']','Shake scotch, juice of lime, and powdered sugar with ice and strain into a whiskey sour glass. Decorate with 1/2 slice lemon, top with the cherry, and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (934,'Scottish Fruit Tart','[''glenrothes vintage reserve'', ''luxardo sangue morlacco cherry liqueur'', ''sweet vermouth'', ''fresh orange juice'']','[''.75 oz'', ''.75 oz'', ''.75 oz'', ''.75 oz'']',NULL);
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (935,'Scottish Highland Liqueur','[''johnnie walker'', ''honey'', ''angelica root'', ''fennel seeds'', ''lemon peel'']','[''1 fifth'', ''1 1/2 cup mild'', ''2 tsp dried and chopped'', ''1/4 tsp crushed'', ''2 2 inch strips'']','Combine all ingreds in aging container. Cover tightly and shake gently several times during the first 24 hrs. After 24 hrs, remove the lemon zest. Cover again and let stand in a cool, dark place for 2 weeks, shaking gently every other day. Strain through a wire sieve to remove the angelica root and fennel. Return to aging container, cover and let stand undisturbed in a cool dark place for 6 months. Siphon or pour clear liqueur into a sterile bottle. The cloudy dregs may be saved for cooking.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (936,'Screaming Orgasm','[''vodka'', "bailey''s irish cream", ''kahlua'']','[''1 oz'', ''1 1/2 oz'', ''1/2 oz'']','Pour first vodka, then Bailey''s, then Kahlua into a cocktail glass over crushed ice. Stir. Caution: use only high quality vodka. Cheap vodka can cause the Bailey''s to curdle. Test your brand of vodka by mixing 1 Tsp each of vodka and Bailey''s first.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (937,'Screwdriver','[''vodka'', ''orange juice'']','[''2 oz'', '' '']','Mix in a highball glass with ice. Garnish and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (938,'Sea breeze','[''vodka'', ''cranberry juice'', ''grapefruit juice'']','[''1 1/2 oz'', ''4 oz'', ''1 oz'']','Build all ingredients in a highball glass filled with ice. Garnish with lime wedge.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (939,'Second City Sour','[''hirsch small batch reserve bourbon'', ''fresh lemon juice'', ''orange-scented maple syrup*'', ''tempus fugit alessio vermouth di torino rosso'']','[''2 oz'', ''1 oz'', ''.5 oz'', ''1 oz'']','Combine bourbon, lemon juice, and syrup in a shaker and add cold ice. Shake until chilled well (10-30 sec. depending on the quality of the ice). Strain into a collins glass and slowly pour the vermouth into the glass. Garnish with a lemon twist. . . *Orange-scented Maple Syrup:. combine 6 oz. of grade B maple syrup, 6 oz. filtered water and the peel of 1 orange, with a minimum amount of pith in a non-reactive pan. . Simmer for 10 mins. Remove from heat and allow it to cool to room temp. before using. . Syrup will keep for 1 month if refrigerated.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (940,'See You T''Amaro','[''luxardo amaro abano'', ''lemon juice'', ''simple syrup'', ''egg white'', ''fee bros rhubarb bitters'']','[''1.5 oz'', ''.5 oz'', ''.25 oz'', ''.5 oz'', ''1 dash'']','Combine all ingredients in shaker. Dry shake for 10 seconds, add ice and shake for 30 seconds. Strain into small tumbler. Garnish with a sprinkle of grated nutmeg.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (941,'See ya Amaro!','[''h by hine'', ''luxardo amaro abano'', ''luxardo apricot liqueur'', ''luxardo amaretto di saschira'', ''luxardo maraschino liqueur'', ''perfect puree apricot*'', ''lemon juice'']','[''1.5 oz'', ''.75 oz'', ''.5 oz'', ''.25 oz'', ''.16 oz'', ''.5 oz'', ''.5 oz'']','Combine all ingredients in a tall glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (942,'Seersucker Sunshine','[''luxardo bitter bianco'', ''seersucker gin'', ''lillet blanc'', ''sauternes'', ''orange blossom water'']','[''.75 oz'', ''1.5 oz'', ''.5 oz'', ''1.5 oz'', ''2 dash'']','Rinse the glass with Orange Blossom Water and pour cocktail. Shake Egg White, Sauternes and Lemon Juice vigorously dry for 25 seconds, then add ice, gin, Luxardo Bitter Bianco and Lillet Blanc, shake again for 10 seconds. Strain into Nick & Nora, garnish with a lemon wheel.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (943,'Sex on the Beach','[''vodka'', ''peach schnapps'', ''cranberry juice'', ''grapefruit juice'']','[''1 oz'', ''3/4 oz'', '' '', '''']','Build all ingredients in a highball glass filled with ice. Garnish with orange slice.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (944,'Shadowcat','[''luxardo bitter bianco'', ''barr hill gin'', ''falernum'', ''topo chico mineral water'', ''lemon bitters'']','[''1 oz'', ''1 oz'', ''.75 oz'', ''1 oz'', ''1 dash'']','Combine ingredients in a pint glass, stir with ice to chill. Double strain into a coupe glass. Garnish with mint and lemon wedge flag.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (945,'Shanghai Cocktail','[''light rum'', ''anisette'', ''grenadine'', ''lemon'']','[''1 oz jamaican'', ''1 tsp'', ''1/2 tsp'', ''juice of 1/4'']','Shake all ingredients with ice, strain into a cocktail glass, and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (946,'Shark Attack','[''lemonade'', ''water'', ''vodka'']','[''1 can'', ''3 cans'', ''1 1/2 cup'']','Mix lemonade and water according to instructions on back of can. If the instructions say to add 4 1/3 cans of water do so. Mix into pitcher. Add 1 1/2 cup of Vodka (Absolut). Mix well. Pour into glass of crushed ice. Excellent!');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (947,'Sherry Eggnog','[''sherry'', ''powdered sugar'', ''egg'', ''milk'', ''nutmeg'']','[''2 oz cream'', ''1 tsp'', ''1 whole'', '''', '''']','Shake sherry, powdered sugar, and egg with ice and strain into a collins glass. Fill with milk and stir. Sprinkle nutmeg on top and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (948,'Sherry Flip','[''sherry'', ''light cream'', ''powdered sugar'', ''egg'', ''nutmeg'']','[''1 1/2 oz cream'', ''2 tsp'', ''1 tsp'', ''1 whole'', '''']','Shake all ingredients (except nutmeg) with ice and strain into a whiskey sour glass. Sprinkle nutmeg on top and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (949,'Short Round','[''bacardi superior rum'', ''chateau de montifaud pineau des charentes blanc'', ''lemon juice'', ''velvet falernum'']','[''5 oz'', ''4 oz'', ''3.5 oz'', ''3.5 oz'']','Build all ingredients in a punch bowl and add ice, stir gently. Garnish with seasonal fruit and citrus wheels.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (950,'Short Round II','[''avuá prata cachaça'', ''bacardi superior rum'', ''chateau de montifaud pineau des charentes rose'', ''lime juice'', ''cannella cinnamon cordial'', ''simple syrup'']','[''2 oz'', ''2 oz'', ''3 oz'', ''2 oz'', ''1 oz'', ''2 oz'']','Build & Stir. Hoshizaki Ice.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (951,'Shot-gun','[''jim beam'', ''jack daniels'', ''wild turkey'']','[''1 part'', ''1 part'', ''1 oz'']','Pour one part Jack Daneils and one part Jim Beam into shot glass then float Wild Turkey on top.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (952,'Shroud of the Inca','[''luxardo bitter bianco'', ''castilian tincture*'', ''barsol supremo moste verde italia pisco'', ''prosecco'']','[''1 oz'', ''.25 oz'', ''.75 oz'', ''2 oz'']','Combine Luxardo, BarSol, and tincture. Ice, stir, and strain into chilled coupe. Top with Prosecco. Gently set candied blood orange wheel (thinly sliced) garnish into drink and serve.. . *Castillian Tincture:. -- 750 ml Luxardo Grappa Euganea. -- 20 g Sliced Blanched Almond. -- 2 g Spanish Saffron. -- 3 Sliced Madagascan Vanilla Beans. -- 3 g Toasted Green Cardamom. -- 4 Lemon Peels (pith removed). -- 2 Quartered Dried Pajarero Figs. -- 2 Quartered Quartered Medjool Dates. . Combine all ingredients, gently agitate twice daily for 14 days. Fine filter and store.. . **Candied Blood Orange Wheels:. -- 1 Blood Orange (sliced thin - approx. 10 slices/orange). -- .5 c Natural Sugar. -- .25 c Water. -- 2 Tbsp Corn Syrup. . Combine sugar, water, and corn syrup in shallow saucepan. Slow simmer on low heat approx. 10 min. When thickened mixture begins to cling to sides of pan, add orange slices. Allow to simmer for 5 more min. and remove from hardening syrup. Place slices on parchment-lined oven rack. Air dry overnight, then bake 10 min. at 400°F (or dehydrate them).');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (953,'Sidecar','[''cognac'', ''cointreau'', ''lemon juice'']','[''2 oz'', ''1/2 oz'', ''1 oz'']','Pour all ingredients into cocktail shaker filled with ice. Shake well and strain into cocktail glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (954,'Sidecar Cocktail','[''brandy'', ''triple sec'', ''lemon'']','[''1 oz'', ''1/2 oz'', ''juice of 1/4'']','Shake all ingredients with ice, strain into a cocktail glass, and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (955,'Sidewinder','[''h by hine'', ''apple brandy'', ''benedictine'', ''yellow chartreuse'', ''vieux pontarlier absinthe francaise superieure'', "peychaud''s bitters"]','[''2 oz'', ''.5 oz'', ''.25 oz'', ''.25 oz'', ''1 dash'', ''1 dash'']','Stir all the ingredients over ice, then strain into a coupe. No garnish.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (956,'Simply Perfecto','[''barsol perfecto amor (aperitif wine)'']','[''2 oz'']','Pour Perfecto Amor over ice and garnish with an orange wheel.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (957,'Singapore Sling','[''cherry brandy'', ''grenadine'', ''gin'', ''sweet and sour'', ''carbonated water'', ''cherry'']','[''1/2 oz'', ''1/2 oz'', ''1 oz'', ''2 oz'', '' '', '' '']','Pour all ingredients into cocktail shaker filled with ice cubes. Shake well. Strain into highball glass. Garnish with pineapple and cocktail cherry.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (958,'Singapore Sling (1915 Recipe)','[''no.3 london dry gin'', ''luxardo sangue morlacco cherry liqueur'', ''luxardo triplum (triple sec)'', ''luxardo fernet'', ''pineapple juice'', ''fresh lime juice'', ''bitters'']','[''1.5 oz'', ''.5 oz'', ''.25 oz'', ''.25 oz'', ''3 oz'', ''.5 oz'', ''2 dash'']','Shake ingredients with ice then strain into an ice-filled collins or pilsner glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (959,'Sloe & Steady','[''c. drouin calvados selection'', ''spirit works sloe gin'', ''bonal'', ''black salt tincture'']','[''1.5 oz'', ''.75 oz'', ''.5 oz'', ''4 drops'']','Stir for about 20 seconds with ice and then Strain into a chilled Nick & Nora glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (960,'Sloe Gin Cocktail','[''sloe gin'', ''dry vermouth'', ''orange bitters'']','[''2 oz'', ''1/4 tsp'', ''1 dash'']','Stir all ingredients with ice, strain into a cocktail glass, and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (961,'Slow Ride','[''rittenhouse straight rye whisky'', "pusser''s rum", "king''s ginger liqueur", ''basic house lemonade*'', ''abita big easy ipa'']','[''1 oz'', ''.5 oz'', ''.5 oz'', ''2 oz'', ''top'']','Shake rye, rum, lemonade, and ginger liqueur. Strain over fresh ice in a copper mug. Top with Abita Big Easy IPA (or similar session IPA). Gently stir.. . *Basic House Lemonade:. -- 2 parts Water. -- 1 part Sugar. -- 1 part Lemon Juice');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (962,'Smashing Great Pumpkins','[''pumpkin vodka'', ''new york apple whisky'', "king''s ginger liqueur", ''hot water'']','[''1.5 oz'', ''.75 oz'', ''.75 oz'', ''.5 oz'']','Steam in steaming tin. Pour in Demitasse glass. Top with heavy whipping cream and nutmeg.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (963,'Smoke On The Dock','[''luxardo fernet'', ''mezcal'', "fresh lime sweet n'' sour mix", ''pomegranate juice'']','[''.75 oz'', ''.34 oz'', ''1.5 oz'', ''2 oz'']','Shake ingredients with ice then strain into a Collins glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (964,'Smut','[''red wine'', ''peach schnapps'', ''pepsi cola'', ''orange juice'']','[''1/3 part'', ''1 shot'', ''1/3 part'', ''1/3 part'']','Throw it all together and serve real cold.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (965,'Snake Bite (UK)','[''lager'', ''cider'']','[''1/2 pint'', ''1/2 pint sweet or dry'']','Pour ingredients into a pint glass. Drink. Fall over.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (966,'Snakebite and Black','[''lager'', ''cider'', ''blackcurrant squash'']','[''1/2 pint'', ''1/2 pint'', ''a little bit of'']','Put blackcurrant squash in first up to about 1cm in glass. Then add the larger and cider one after another.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (967,'Snow Bunny','[''christmas spirit'', "king''s ginger liqueur", ''lemon juice'', ''honey syrup'', ''egg white'', ''ale'', "dale degroff''s pimento aromatic bitters"]','[''1.5 oz'', ''.5 oz'', ''.5 oz'', ''.5 oz'', ''.5 oz'', ''2 oz'', ''3 drops'']','Combine all ingredients besides beer and bitters and dry shake without ice. Add ice and re-shake, double strain into a fizz glass and top with beer. Garnish with shaved nutmeg and 3 drops of bitters.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (968,'Snowball','[''advocaat'', ''lemonade'', ''lemon'', ''ice'']','[''1 1/2 oz'', ''8-10 oz cold'', ''1 slice'', ''cubes'']','Place one ice cube in the glass and add 1 1/2 oz of Advocaat. Fill up the glass with lemonade and decorate with a slice of lemon. Serve at once.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (969,'Sol Y Sombra','[''brandy'', ''anisette'']','[''1 1/2 oz'', ''1 1/2 oz'']','Shake ingredients with ice, strain into a brandy snifter, and serve. (The English translation of the name of this drink is "Sun and Shade", and after sampling this drink, you''ll understand why. Thanks, Kirby.)');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (970,'Soleo','[''luxardo limoncello'', ''herbal liqueur'']','[''1 oz'', ''1 oz'']','Use an empty bottle of 750ml capacity and combine together 12 ounces - 360 ml of each of the two liqueurs and store in the freezer. Serve very cold in a small cordial glass without ice.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (971,'Solera Sidecar','[''h by hine'', ''lustau east india solera'', ''grand marnier'', ''luxardo amaretto di saschira'', ''lemon juice'', ''simple syrup'']','[''1.5 oz'', ''.5 oz'', ''.25 oz'', ''.25 oz'', ''.5 oz'', ''.25 oz'']','Shake all the ingredients with ice, then strain into a coupe. No garnish.');
