INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (984,'Spiking coffee','[''coffee'', ''cinnamon'']','['''', '''']','Incidentally, a pinch of cinnamon is indeed a nice addition to coffee but true heaven is a cardamom seed. Of course, you serve it in a coffee mug.');
