INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (997,'Strawberry Margarita','[''strawberry schnapps'', ''tequila'', ''triple sec'', ''lemon juice'', ''strawberries'', ''salt'']','[''1/2 oz'', ''1 oz'', ''1/2 oz'', ''1 oz'', ''1 oz'', '''']','Rub rim of cocktail glass with lemon juice and dip rim in salt. Shake schnapps, tequila, triple sec, lemon juice, and strawberries with ice, strain into the salt-rimmed glass, and serve.');
