INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1009,'Sweet Tooth','[''godiva liqueur'', ''milk'']','[''2 shots'', '''']','Put 2 shots Godiva Liquour into a glass, add as much or as little milk as you would like.');
