INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1014,'Tambourine','["karlsson''s gold vodka", ''pineapple juice'', ''fresh lime juice'', ''kaffir lime & tamarind compound syrup*'']','[''2 oz'', ''.75 oz'', ''.5 oz'', ''.5 oz'']','In a Boston shaker, combine all liquid ingredients with cracked ice and shake vigorously for 30 seconds. Double strain into a frozen 4.5 oz. coupe glass. Garnish with 1/2 piece kaffir lime leaf. (Clap the leaf several times before placing on top of the cocktail to release the aromatics.)');
