INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1016,'Tea Party Cocktail','[''no.3 london dry gin'', "king''s ginger liqueur", ''orange marmalade syrup'', ''lemon juice'', ''bergamot tea foam'']','[''1.5 oz'', ''.5 oz'', ''.5 oz'', ''.25 oz'', ''1 oz'']','Shake all ingredients except foam, strain into chilled cocktail "coupe". Layer foam onto cocktail. Add garnish.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1017,'Temple of the Moon','[''junipero gin'', ''barsol primero quebranta pisco'', ''amaro sibilla'', ''simple syrup'', ''st. george absinthe'', ''fee brothers orange flower water'']','[''1 oz'', ''1 oz'', ''1 oz'', ''.25 oz'', ''rinse'', ''3 drops'']','Stir all ingredients except for Absinthe and Orange Flower. Strain over a chunk/sphere of ice in an old fashioned glass. Garnish with a sprig of mint, with 3 drops of Orange Flower.');
