INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1019,'Tequila Fizz','[''tequila'', ''lemon juice'', ''grenadine'', ''egg white'', ''ginger ale'']','[''2 oz'', ''1 tblsp'', ''3/4 oz'', ''1'', '''']','Shake all ingredients (except ginger ale) with ice and strain into a collins glass over ice cubes. Fill with ginger ale, stir, and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1020,'Tequila Sour','[''tequila'', ''lemon'', ''powdered sugar'', ''lemon'', ''cherry'']','[''2 oz'', ''juice of 1/2'', ''1 tsp'', ''1/2 slice'', ''1'']','Shake tequila, juice of lemon, and powdered sugar with ice and strain into a whiskey sour glass. Add the half-slice of lemon, top with the cherry, and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1021,'Tequila Sunflare','[''chinaco blanco tequila'', ''sage-cilantro-lime syrup'', ''montanaro vermouth'', ''cube'']','[''1.5 oz'', ''.75 oz'', ''rinse'', ''1  pomegranate ic'']','Pre-freeze pomegranate juice into one large cube. Rinse a glass with Montanaro vermouth. Dump any excess out. Build the remainder of spirits and bitters into a shaker tin. Shake vigorously. Strain over the pomegranate ice cube into a rocks glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1022,'Tequila Sunrise','[''tequila'', ''orange juice'', ''grenadine'']','[''2 measures'', '' '', '' '']','Pour the tequila and orange juice into glass over ice. Add the grenadine, which will sink to the bottom. Stir gently to create the sunrise effect. Garnish and serve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1023,'Tequila Surprise','[''tequila'', ''tabasco sauce'']','[''full glass'', ''about 8 drops'']','Fill shot glass with Tequila. Add drops of Tobasco sauce.');
