INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1025,'Texas Sling','[''kahlua'', ''irish cream'', ''amaretto'', ''151 proof rum'', ''cream'']','[''1/2 oz'', ''1/2 oz'', ''1/2 oz'', ''1/2 oz bacardi'', ''1 oz'']','Blend with Ice until smooth. Serve in a tulip glass, top with whip cream.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1026,'Thai Coffee','[''coffee'', ''coriander'', ''cardamom'', ''sugar'', ''whipping cream'', ''ice'']','[''6 tblsp ground'', ''1/4 tsp'', ''4-5 whole green'', '' '', '' '', '' '']','Place the coffee and spices in the filter cone of your coffee maker. Brew coffee as usual, let it cool. In a tall glass, dissolve 1 or 2 teaspoons of sugar in an ounce of the coffee (it''s easier to dissolve than if you put it right over ice). Add 5-6 ice cubes and pour coffee to within about 1 inch of the top of the glass. Rest a spoon on top of the coffee and slowly pour whipping cream into the spoon. This will make the cream float on top of the coffee rather than dispersing into it right away.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1027,'Thai Iced Coffee','[''coffee'', ''sugar'', ''cream'', ''cardamom'']','[''black'', '''', ''pods'', '''']','Prepare a pot of coffee at a good European strength. In the ground coffee, add 2 or 3 freshly ground cardamom pods. Sweeten while hot, then cool quickly. Serve in highball glass over ice, with cream. To get the layered effect, place a spoon atop the coffee and pour the milk carefully into the spoon so that it floats on the top of the coffee.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1028,'Thai Iced Tea','[''tea'', ''water'', ''condensed milk'', ''ice'', ''mint'']','[''1/4 cup thai'', ''1/2 cup boiling'', ''2 tsp sweetened'', ''cubes'', '''']','Combine Thai tea (i.e., the powder), boiling water, and sweetened condensed milk, stir until blended. Pour into 2 tall glasses filled with ice cubes. Garnish with mint leaves. Makes 2 servings.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1029,'The 42nd Round','[''junipero gin'', ''compas box great king street blended scotch whisky'', ''byrrh grand quinquina'', ''anchor christmas ale 2016*'', ''bittercube corazon bitters'', ''the bitter truth orange bitters'']','[''1 oz'', ''5 oz'', ''1 oz'', ''.5 oz'', ''2 dash'', ''2 dash'']','Mix all ingredients in a mixing glass and stir for 15-20 secs depending on the type of ice. Strain over a large rock and garnish with an orange peel expressing the oils over the cocktail.. . *Anchor Christmas Ale 2016 w/ Honey (reduction) 2:1 (2-parts Beer to 1-part Honey)');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1030,'The Alpine Mist','[''mezcal'', "reed''s premium ginger brew", ''seedless blackberry jam'', ''lime'', ''d'', ''mint leaves'', ''muddled'']','[''2 oz'', ''2 oz'', ''1 tsp'', ''1'', ''juic'', ''12'', ''muddle'']','Fill a cocktail shaker with ice. Add all ingredients except the garnish and shake well. Stain into a very chilled martini glass (martini glass should have a sugar rim on half of the glass) and top with a splash of ginger beer. Garnish with a mint leaf and orange peel. Right before serving drop in a chip of dry ice for the mist.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1031,'The Asterisk','[''brandy'', ''green chartreuse'', ''luxardo maraschino liqueur'', ''lemon juice'']','[''.75 oz'', ''.75 oz'', ''.75 oz'', ''.75 oz'']','Shake until very cold; double strain into a chilled cocktail glass. No garnish.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1032,'The Awen','[''pot still-style irish whiskey'', ''luxardo maraschino liqueur'', ''cocchi americano'', ''orange bitters'']','[''1.5 oz'', ''.5 oz'', ''1 oz'', ''1 dash'']','Stir ingredients in glass over an ice sphere.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1033,'The Bacchus Cocktail','[''rd grapes'', ''berries'', ''barsol primero quebranta pisco'', ''crème yvette'', ''lemon juice'', ''egg white'']','[''5  conc'', ''3  blac'', ''1.5 oz'', ''1 oz'', ''1 oz'', ''.5 oz'']','Place the Concord grapes and blackberries in a shaker and muddle. Add the rest of the ingredients and ice. Shake, and strain into a chilled coupe. Garnish with Concord grapes and lemon peel twisted to release oils.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1034,'The Big Picture','[''tempus fugit alessio vino chinato'', ''fig-infused c. drouin calvados selection'', ''lemon'', ''simple syrup'']','[''1.5 oz'', ''1.5 oz'', ''.5 oz'', ''.25 oz'']','Build in glass. Add crushed ice and swizzle. Top with more crushed ice. Dash pepita tincture on top for aromatics. Garnish with lemon wheel and dried fig.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1035,'The Bitter King','["king''s ginger liqueur", ''lemon juice'', ''simple syrup'']','[''2 oz'', ''.75 oz'', ''.75 oz'']','Add all ingredients to a shaker, add ice, shake and strain into a martini glass garnish with a Luxardo Cherry.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1036,'The Bittered Valley','[''luxardo maraschino liqueur'', ''acid phosphate'', ''barsol primero quebranta pisco'', ''luxardo amaro abano'', ''luxardo fernet'', "scrappy''s aromatic bitters"]','[''1.25 oz'', ''4 drops'', ''2 oz'', ''.75 oz'', ''.25 oz'', ''3 dash'']','1st glass ingredients:. Luxardo Maraschino, Acid Phosphate. . 2nd glass ingredients:. BarSol Quebranta Pisco, Luxardo Amaro Abano, Luxardo Fernet, Scrappy''s Aromatic Bitters');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1037,'The Bossa Nova','[''chinaco blanco tequila'', ''lemon juice'', ''small hand foods pineapple gum syrup'', ''vanilla syrup*'', ''coconut cream**'', ''egg white'']','[''2 oz'', ''.5 oz'', ''.5 oz'', ''.25 oz'', ''.5 oz'', ''.5 oz'']','Combine all ingredients in a shaking tin or mixing glass and vigorously shake without ice for 10 seconds. Add ice, and shake for another 8 seconds. Strain over larger ice cube or into a cocktail glass and add freshly grated cinnamon on top.. . *Vanilla Syrup: . Split 1 vanilla pod lengthwise and scrape interior into a heavy sauce pan. . Add 2 c. raw sugar and 2 c. water. Bring to a simmer and reduce heat to low. . Cook for 5 mins and stir occasionally. . Remove from heat and keep unstrained for 24 hrs. . Strain through a cheese cloth and bottle. Keeps for 2 weeks.. . **Coconut Cream:. Combine 1 part Coco Lopez with 1 part rich coconut milk. Mix well.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1038,'The Boys of Summer Cup','[''no.3 london dry gin'', ''dolin rouge vermouth'', ''the genuine curacao liqueur orange'', "king''s ginger liqueur", ''chareau liqueur'', ''fresh lemon juice'', ''fever-tree bitter lemon'']','[''1 oz'', ''1 oz'', ''.5 oz'', ''.5 oz'', ''.25 oz'', ''.5 oz'', ''top'']','Top with Fever-Tree Bitter Lemon. Build in a collins glass full of ice cubes and garnish with a lemon wheel and mint sprig.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1039,'The Brightside','[''pink pigeon rum'', ''fresh orange juice'', ''luxardo bitter'', ''milk powder (or cream)'']','[''1.5 oz'', ''2.5 oz'', ''1 tbsp'', ''1 dash'']','Dry shake, then serve old-fashioned glass with crushed ice. Drizzle Bitter on top to finish.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1040,'The Callas Cocktail','[''luxardo bitter bianco'', ''no.3 london dry gin'', ''pomegranate cordial*'', ''brachetto'', ''sugar cube'', "tempus fugit abbott''s bitters"]','[''1 oz'', ''.75 oz'', ''1 bsp'', ''1.25 oz'', ''1'', ''8 dash'']','Soak cube in 8 dashes Abbott''s Bitter. Drop sugar cube into bottom of flute glass. Stir, Luxardo, gin, and pomegranate cordial. Strain into flute glass. Top with Brachetto. Garnish with small rose, dipped in rose flower water.. . *Pomegranate Cordial:. -- 48 oz Pomegranate Juice. -- 2.5 lbs White Sugar. -- 1 lb Turbinado Sugar. -- 8 Course Cracked Black Peppercorns. -- 2 Cloves. -- 5 Allspice Berries. -- 1 Tbsp Pomegranate Molasses. -- 1 oz Orange Flower Water. . Mix all ingredients together in large saucepan. Stir on medium heat until all sugar is dissolved. Strain of spices. Bottle.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1041,'The Canevari Cocktail','[''old tom gin'', ''cocchi vermouth di torino'', ''luxardo bitter'', ''angostura bitters'']','[''1.5 oz'', ''1 oz'', ''.5 oz'', ''3 dash'']','Combine ingredients, stir until well chilled, and strain in to a cocktail glass. Serve with an orange twist.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1042,'The Cappa','[''gin'', ''pineau des charentes'', ''luxardo maraschino liqueur'']','[''1.5 oz'', ''1.5 oz'', ''.5 oz'']','Add all the ingredients to a mixing glass and fill with ice. Stir, and strain into a small wine or cocktail glass. Twist a slice of lemon peel over the drink and use as garnish.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1043,'The Cavalier White Lady','[''junipero gin'', ''creole shrub'', ''lemon juice'', ''lemon bitters'', ''simple syrup'', ''egg white'']','[''1 oz'', ''.75 oz'', ''.75 oz'', ''.25 oz'', ''.5 oz'', ''1'']','Spray of Lemon Verbana oil on the Martini glass. Combine all other ingredients and dry shake for 20 seconds. Add ice to the mixing tin and shake again. Double strain into a Martini glass. Garnish with fresh lemon verbena. Served in a martini.. .');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1044,'The Chief','[''junipero gin'', ''cocchi americano'', ''salers gentiane aperitif'', ''black licorice-infused angostura bitters'', ''ure'']','[''1.5 oz'', ''.75 oz'', ''.5 oz'', ''2 drops'', ''1 drop sea salt tinc'']','Stir the first three ingredients and the sea salt tincture over ice. Strain into a coupe glass and add licorice-infused Angostura Bitters. Garnish with 3 brandied cherries.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1045,'The Claridge','[''gin'', ''dry vermouth'', ''luxardo apricot liqueur'', ''cointreau'']','[''2.25 oz'', ''2.25 oz'', ''.75 oz'', ''.75 oz'']','Stir, strain, and serve straight up.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1046,'The Cliffdiver','["d''aristi 10 yr rum", ''the genuine curacao liqueur blue'', ''tempus fugit liqueur de violettes'', ''lime juice'', ''tahitian vanilla bean syrup'', ''ginger beer'', ''angostura bitters'']','[''1.25 oz'', ''.5 oz'', ''.25 oz'', ''1 oz'', ''.5 oz'', ''1 float'', ''3 dash'']','Combine rum, curacao, violettes, and lime juice in a shaker, add ice and shake vigorously. Double strain into a chilled collins glass. Add fresh ice in the collins glass and top with chilled ginger beer. Float 2-3 dashes of Angostura Bitters on top and garnish with a grilled pineapple wedge on rim of glass, and two Luxardo Cherries speared through a toothpick, and edible orchid placed on top hanging from the rim.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1047,'The Commissioner''s Trophy #3','[''no.3 london dry gin'', ''amaro'', ''barsol perfecto amor (aperitif wine)'', ''strawberry shrub'']','[''1.5 oz'', ''1 oz'', ''.5 oz'', ''.5 oz'']','Stir in a mixing glass with ice; strain into a coupe glass; garnish with orange and lemon zest.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1048,'The Double Crown','[''compass box great king street glasgow blend'', ''lemon juice'', ''cyril zangs double zero eau de vie de cidre'', "king''s ginger liqueur", ''honey syrup'', ''egg white'']','[''1.25 oz'', ''.75 oz'', ''.5 oz'', ''.5 oz'', ''.5 oz'', ''1'']','Shake without ice, add ice and shake again then fine strain into a chilled egg coupe. Garnish with a Sage leaf.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1049,'The Douglas Tilden','[''junipero gin'', ''lacuesta blanco vermouth'', ''salers gentiane aperitif'', ''luxardo maraschino liqueur'', ''germain-robin absinthe'', ''scrappy’s lime bitters'']','[''2 oz'', ''.5 oz'', ''.25 oz'', ''1 bsp'', ''1 bsp'', ''2 dash'']','Combine all ingredients in a mixing glass with ice, stir and strain into a chilled cocktail glass. Garnish with a lemon twist and the edible flower of your choice.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1050,'The Dry Cruise','[''luxardo bitter bianco'', ''lustau oloroso sherry'', ''st. germain'', ''lemon juice'', ''honey syrup'', ''sparkling wine'']','[''1 oz'', ''1 oz'', ''.25 oz'', ''.75 oz'', ''.5 oz'', ''top'']',NULL);
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1051,'The Evil Blue Thing','[''creme de cacao'', ''blue curacao'', ''light rum'']','[''1 1/2 oz'', ''1 oz'', ''1/2 oz'']','Pour ingredients into glass, and drop in a blue whale! The blue whale isn''t really necessary, but it makes the drink more "fun".');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1052,'The Fall 75','[''junipero gin'', ''spiced kuri squash purée*'', ''fresh lemon'', ''honey syrup'']','[''1 oz'', ''1 oz'', ''2 dash'', ''2 dash'']','Shake, strain into coupe glass. Top with sparkling wine and a sprig of lemon balm.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1053,'The Fifth Star','[''nikka whisky taketsuru pure malt 12 yo'', ''luxardo amaretto di saschira'', ''luxardo bitter'', ''luxardo fernet'', ''lustau dry olorosso sherry'', ''la colombe cold draft coffee'']','[''1.5 oz'', ''.5 oz'', ''.5 oz'', ''.5 oz'', ''.25 oz'', ''oz'']',NULL);
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1054,'The French Connection','[''h by hine'', ''fresh lemon juice'', ''demarara simple syrup'', ''apple bitters'']','[''2 oz'', ''1 oz'', ''1 oz'', ''4 dash'']','Shake ingredients with ice in shaker and then double strain into a couple glass.. Top with champagne and garnish with lemon and apple slice.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1055,'The Fritz','[''junipero gin'', ''luxardo maraschino liqueur'', "peychaud''s bitters", ''punt e mes'']','[''.75 oz'', ''.75 oz'', ''.75 oz'', ''.75 oz'']','Stir with ice and strain into a rocks glass. Garnish with an orange twist.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1056,'The Ginger Royale','["king''s ginger liqueur", ''champagne'']','[''35 ml'', ''140 ml'']','Pour a generous measure of The King''s Ginger into a flute. Add chilled Champagne to taste.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1057,'The Great Pumpkin Cocktail','[''pumpkin-infused glenrothes vintage reserve*'', "king''s ginger liqueur", ''fresh lemon juice'', "grade-a maple syrup (prefer mcclure''s)"]','[''1 oz'', ''1 oz'', ''.5 oz'', ''.5 oz'']','Shake over ice and strain into a chilled cocktail coupe. Garnish with a dash of cinnamon.. . *Pumpkin-Infused Glenrothes Vintage Reserve:. Peel, clean and cut small 2x2 in. pieces of sugar pumpkin. Salt very lightly and roast for about 20 mins. at 250°F. Put into a sealed container with scotch and let rest for 2 days. Strain off the pumpkin and re-bottle. Keep refrigerated.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1058,'The Greatest Punch on Earth','[''don cesar pisco'', ''luxardo triplum (triple sec)'', ''apple cider'', ''oloroso sherry'', ''sherry vinegar'', ''orange bitters'', ''bitters'']','[''4 oz'', ''1 oz'', ''4 oz'', ''2 oz'', ''1 oz'', "regans''", ''angostura'']',NULL);
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1059,'The Happy Place','[''junipero gin'', ''house-made cranberry syrup*'', ''lemon juice'', ''cranberry juice'', ''lillet blanc'', ''forgery earth day bitters'', ''laphroaig'']','[''2 oz'', ''.75 oz'', ''.5 oz'', ''.5 oz'', ''.25 oz'', ''4 dash'', ''mist'']','*House-made Cranberry syrup: . -- 2 cups Fresh Cranberries. -- 1 cup Sugar. -- 1 cup Water. -- 2 Bay Leaves. -- .25 cup Pink Peppercorns. -- Half Serrano Chile. -- 4 Sprigs Fresh Rosemary. . Add all ingredients to a pot and heat thoroughly. Simmer on low until cranberries cook down for 25 minutes. Strain and let cool.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1060,'The Hollinger Manhattan','[''rittenhouse rye'', ''sweet vermouth'', ''luxardo maraschino liqueur'', ''duplais swiss absinthe verte'']','[''2 oz'', ''.5 oz'', ''.25 oz'', ''.25 oz'']','Stir, strain into an absinthe rinsed coupe. Garnish with Luxardo Cherry.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1061,'The House of Duncan','[''glenrothes vintage reserve'', ''luxardo sangue morlacco cherry liqueur'', "king''s ginger liqueur", ''angostura bitters'']','[''1.5 oz'', ''.5 oz'', ''.25 oz'', ''1 dash'']','Serve on the rocks. Garnish with a Luxardo Cherry.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1062,'The Improved Sazerac','[''h by hine'', ''luxardo maraschino liqueur'', ''simple syrup (1:1)'', "peychaud''s bitters", ''angostura bitters'', ''ise superieure'']','[''1.5 oz'', ''.25 oz'', ''.5 tsp'', ''3 dash'', ''1 dash'', ''rinse vieux pontarlier absinthe franc'']','Put ingredients in mixing glass. Stir. Strain into glass that has absinthe rinse. Add lemon zest.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1063,'The Inside Job','[''bourbon'', ''luxardo maraschino liqueur'', ''simple syrup'', ''angostura bitters'', ''vieux pontarlier absinthe francaise superieure'']','[''2 oz'', ''.25 oz'', ''.5 oz'', ''1 dash'', ''1 dash'']','Add all the ingredients to a mixing glass and fill with ice. Stir, and strain into an old fashioned glass filled with fresh ice. Garnish with a Luxardo Cherry and an orange slice or twist.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1064,'The Italian Diplomat','[''luxardo bitter bianco'', ''barsol primero quebranta pisco'', ''bay leaf syrup*'', ''salt tincture**'']','[''1 oz'', ''1.5 oz'', ''.25 oz'', ''2 dash'']','Combine all ingredients in mixing glass. Stir and strain into Nick & Nora cocktail glass. Garnish with lemon zest and yerba mate tea sachet.. . *Bay Leaf Syrup:. -- 16 oz Water. -- 12 Bay Leaves. -- 1.5 C White Sugar. . Bring water to boil with bay leaves. Lower heat to medium and simmer 15-20 mins. Stir in white sugar until dissolved, without bringing to a boil again. Remove from heat, let cool at room temperature. Remove bay leaves and enjoy.. . **Salt Tincture:. -- .5 C Salt. -- 1 C Water. -- 2 C 100p Vodka. . Stir salt with water and vodka. Stir until dissolved. Strain out extra salt, if any.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1065,'The King Merlet Sidecar','[''h by hine'', ''luxardo triplum (triple sec)'', "king''s ginger liqueur", ''fresh lemon juice'', ''pressed apple juice'']','[''1.5 oz'', ''.5 oz'', ''.5 oz'', ''.5 oz'', ''1 oz'']','Shake all ingredients with ice and strain into an iced up glass with brown sugar rim.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1066,'The King and Tonic','["king''s ginger liqueur", ''fever-tree tonic water'', ''angostura bitters'']','[''1.5 oz'', ''3 oz'', ''1 dash'']','Serve over ice in a collins glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1067,'The King''s Autumn Cobbler','["king''s ginger liqueur", ''briottet creme de mure (blackberry)'', ''pear cider'']','[''1 oz'', ''.25 oz'', ''top'']','Add all ingredients over ice. Stir with a barspoon. Squeeze lemon wedges and add blackberries.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1068,'The King''s Cranberry Sauce','[''pkg'', ''cranberries'', ''brown sugar'', ''orange juice'', "king''s ginger liqueur", ''pepper corns'', ''berries'', ''cloves'', '''', ''in cinnamon'']','[''1'', ''14 oz'', ''1.5 c'', ''.5 c'', ''.3 c'', ''8 whole'', ''6  allspic'', ''5'', ''1 stic'', ''2'']','Heat cranberries, sugar, OJ and King''s Ginger over medium heat. Place the peppercorns, allspice, cloves and cinnamon in a piece of cheesecloth with the end tied. Add to pot. Cook stirring often until cranberries soften about 25 to 30 min. Refrigerate for 1 hour before serving.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1069,'The King''s Egg Nog','[''no.3 london dry gin'', "king''s ginger liqueur", ''english harbour rum'', ''sugar'', ''egg'', ''milk'', ''cream'']','[''1 oz'', ''.5 oz'', ''.25 oz'', ''3 spoon'', ''1 whole'', ''.5 oz'', ''.5 oz'']','Dry shake and then shake with hand cracked ice. Serve in a goblet with grated nutmeg.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1070,'The King''s Froth & Frizz','[''barsol selecto italia pisco'', "king''s ginger liqueur", ''peach brandy'', ''lemon juice'', ''egg white'']','[''1 oz'', ''1.25 oz'', ''.5 oz'', ''.5 oz'', ''.5 oz'']','Dry shake, then shake with ice. Double strain into flute or sour glass. Top with a Saison.. . Zest orange over top and garnish with a star anise..');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1071,'The King''s Ginger Spiced Punch','["king''s ginger liqueur", ''cranberry juice'', ''fever-tree lemonade'']','[''25 oz'', ''37.5 oz'', ''37.5 oz'']','Add all ingredients into a punch bowl or pitcher and stir. Add lemon wheels and strawberries to garnish.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1072,'The King''s Ginger Summer Cup','["king''s ginger liqueur", ''cranberry juice'', ''fever-tree lemonade'']','[''1 oz'', ''1.5 oz'', ''1.5 oz'']','Build in a glass over ice. Add lemon wheels and strawberries. Finish with a splash of fresh lemon or top up with some sparkling wine. This drink can also be made in jugs or punch bowls.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1073,'The King''s Ginger Truffles','[''ps good quality dark chocolate'', ''p double cream'', "the king''s ginger", ''finely chopped drained stem ginger in syrup'']','[''1 1/2 c'', ''1 c'', ''5 tbsp'', ''1 tsp'']',NULL);
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1074,'The King''s Medicine','["king''s ginger liqueur", ''luxardo fernet'', ''benriach curiositas single malt 10 yo'', ''lemon juice'']','[''1.5 oz'', ''.5 oz'', ''.25 oz'', ''.75 oz'']','Add all ingredients into a shaker with ice, shake furiously. Fine strain into chilled coupe. Garnish with a lemon twist.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1075,'The King''s Mulled Cider','["king''s ginger liqueur", ''hard apple cider'', ''fresh apple juice'']','[''2 oz'', ''5 oz'', ''5 oz'']','Serve in a handled glass, hot or over ice. Garnish with lemon, cinnamon stick and fresh blackberries.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1076,'The King''s Mulled Wine','[''red wine'', "king''s ginger liqueur", ''caster sugar'', ''lemons'', ''(plus flesh of one lemon)'', ''orange'', ''zested'', ''cinnamon'', ''nutmeg'', ''star anise'']','[''1 bottle'', ''.3 bottle'', ''3 tbsp'', ''2'', ''zested'', ''1'', ''zeste'', ''1 stic'', ''1 dash'', ''1'']','Simmer over low heat for 3 hours, double strain with fine sieve.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1077,'The King''s Queen','["king''s ginger liqueur", ''fresh lime'', ''fresh ginger'', ''strawberries strawberries(muddled)'']','[''50 ml'', ''25 ml'', ''1 slic'', ''4'']','Serve in a tall glass over ice.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1078,'The King''s Spring Daisy','["king''s ginger liqueur", ''h by hine'', ''lemon juice'', ''simple syrup'', ''fee brothers rhubard bitters'']','[''1 oz'', ''1 oz'', ''.25 oz'', ''.25 oz'', ''2 dash'']','Add all ingredients into a cocktail shaker. Shake and double strain into a glass. Garnish with a sprig of mint.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1079,'The King''s Squeeze','["king''s ginger liqueur", ''fever-tree bitter lemon'']','[''35 ml'', ''125 ml'']','Build in ice-filled tall glass, garnish with a squeeze of lime.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1080,'The King''s Winter Cup','["king''s ginger liqueur", ''fresh pressed apple cider'', ''hard apple cider'', ''cinnamon stick'']','[''1.5 oz'', ''1 oz'', ''4 oz'', ''1 stic'']','Method:  Simmer on a gentle heat for 30 minutes and garnish with a cinnamon stick');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1081,'The Kings Mule','["king''s ginger liqueur", ''orange bitters'', ''sparkling wine'']','[''1 oz'', ''3 dash'', ''5 oz'']','Place King''s Ginger and orange bitters in a Champagne flute, and top with sparkling wine. No garnish.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1082,'The Lady Whisper','[''aged rum'', ''old potrero straight rye whiskey'', ''luxardo apricot liqueur'', ''luxardo sangue morlacco cherry liqueur'', ''bitters'']','[''1 oz'', ''1 oz'', ''.5 oz'', ''.5 oz'', ''2 dash'']','Stir the ingredients in a mixing glass filled with ice, strain and serve over ice into an old fashioned glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1083,'The Ladybird','[''pink pigeon rum'', ''fee brothers black walnut bitters'', ''luxardo sangue morlacco cherry liqueur'', ''luxardo fernet'']','[''2 oz'', ''2 dash'', ''.5 oz'', ''.5 oz'']','In a mixing glass, add the first three ingredients over ice. Stir to chill. In a separate coupe glass, add .5 oz of Luxardo Fernet and swirl glass until it is fully coated with the Fernet. Strain chilled cocktail from mixing glass into the Fernet-rinsed coupe (discard excess fernet). Garnish by placing a Luxardo Cherry into the bottom of the glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1084,'The Last Marasca','[''gin'', ''luxardo maraschino liqueur'', ''luxardo orgeat syrup'', ''lemon juice'', ''absinthe'']','[''50 ml'', ''20 ml'', ''5 ml'', ''5 ml'', ''1 dash'']','Rinse glass with absinthe. Add a Luxardo Cherry (covered in its juice) to a mixing glass with remaining ingredients. Stir until cold and slightly rose colored. Single strain, serve with cherry from mixing glass.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1085,'The Last Tango','[''junipero gin'', ''giffard elderflower liqueur'', ''lime juice'', ''chili tincture*'', ''ino cherries'']','[''1.5 oz'', ''1 oz'', ''1 oz'', ''8-9 drops'', ''6  luxardo marasc'']','Combine the lime, elderflower, tincture and cherries into a shaker tin. Muddle the cherries and then add the Junipero Gin. Shake with ice. Double strain into a chilled cocktail glass. Garnish with a lime wheel.. . *Chili Tincture:. -- Chiles (Habaneros or Bird’s eye Chiles recommended). -- Grain Alcohol or Vodka. . Fill a small jar with stemmed hot chili peppers and cover with grain alcohol or high-proof vodka. Let soak for 3 days; strain.');
