INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1189,'Yoghurt Cooler','[''yoghurt'', ''fruit'', ''ice'']','[''1 cup'', ''1 cup'', '''']','Place all ingredients in the blender jar - cover and whiz on medium speed until well blended. Pour in one tall, 2 medium or 3 small glasses and drink up. Note: Use lots of ice in this one - great on hot days! To add ice: Remove the center of the cover while the blender is on - drop 3 or 4 ice cubs and blend until they''re completely crushed.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1190,'You Bet Your Ascot','[''h by hine'', ''bonal'', ''luxardo amaro abano'', ''nocino'']','[''1.5 oz'', ''.75 oz'', ''.5 oz'', ''.25 oz'']','Combine all ingredients in a mixing glass. Stir until 2 oz water is diluted. Strain into a sherry or nosing glass. Flame and discard an orange peel. Drink deeply.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1191,'You Go Glen Coco','[''old potrero straight rye whiskey (toasted coconut infused)*'', "king''s ginger liqueur", ''turmeric coconut cream**'']','[''1.25 oz'', ''.5 oz'', ''1.25 oz'']','Shaken slightly. Served over shaved ice in an old fashioned glass. Garnished with turmeric toasted coconut flakes.. . *Toasted Coconut Infused Old Potrero. -- 750ml Old Potrero Straight Rye Whiskey. -- 2.5 oz Toasted Coconut. . Combine all ingredients in a clean container and let sit for 8 hours.. Strain through cheesecloth and then funnel back into your Old Potrero bottles.. Keeps indefinitely.. . **Turmeric Coconut Cream:. -- 12 g Fresh Turmeric. -- 1 c Water. -- 1 c Honey. -- 3 tsp Black Pepper. -- 12 oz Coconut Milk. . Grate fresh turmeric into 1 cup water.. Bring to a boil, add 1 cup of honey and stir.. Remove from heat and let the mix sit for 1:30 hours.. Strain out turmeric and add 3 tsp. ground black pepper.. Add 12 oz. coconut milk and stir.. Place in an airtight container and keep in the fridge. Use within a week.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1192,'You''re Bitter Than That','[''blanco tequila'', ''luxardo bitter bianco'', ''luxardo sangue morlacco cherry liqueur'', ''fresh lime juice'', ''rich simple syrup (2:1)'']','[''.75 oz'', ''.75 oz'', ''.75 oz'', ''.75 oz'', ''.25 oz'']','Mount all ingredients in a shaker tin. Add ice. Hard shake. Double strain into a chilled coupe glass. Garnish with orange twist.');
INSERT INTO mytable(FIELD1,drink_name,ingredients,quantities,instructions) VALUES (1193,'Ypioca Chile Punch','[''brasilizar cachaca prata classica'', '''', ''part agave'', ''o reyes ancho chile liqueur'']','[''2 part ypioc'', ''1 part fresh lime juic'', ''.5'', ''.5 part anc'']','For a punch, combine Ypioca, lime, agave and Ancho Reyes together. Add fresh pineapple chunks, 3 thin sliced chiles (seeds included) and 1 star anise to garnish.');
